import React, {Component} from 'react'
// import Button from '@material-ui/core/Button';
// import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Slider from '@material-ui/core/Slider';
import Select from '@material-ui/core/Select';
import './PaletteNavbar.css'

class PaletteNavbar extends Component {
    static defaultProps = {
        marks: [{value: 50, label: '50'},
            {
              value: 100,
              label: '100',
            },
            {
              value: 200,
              label: '200',
            },
            {
              value: 300,
              label: '300',
            },
            {
                value: 400,
                label: '400',
              },
              {
                value: 500,
                label: '500',
              },
              {
                value: 600,
                label: '600',
              },
              {
                value: 700,
                label: '700',
              },
              {
                value: 800,
                label: '800',
              },
              {
                value: 900,
                label: '900',
              },
          ]
    }

    constructor(props){
        super(props);
        this.state={
            value: "500"
        }
          
        this.handleChange = this.handleChange.bind(this)
        this.log = this.log.bind(this)
    }

    handleChange(evt){
        this.props.handleFormat(evt.target.value)
    }
    log(value) {

        console.log(value); //eslint-disable-line
      } 
    render(){
            
        return(
            <div className="PaletteNavbar">
                <div className="PaletteNavbar-slider">
                    <Slider 
                        defaultValue={500}
                        aria-labelledby="discrete-slider"
                        valueLabelDisplay="auto"
                        step={100}
                        marks={this.props.marks}
                        min={50}
                        max={900}
                        onChange={this.log}
                    />
                </div>
                <div className="PaletteNavbar-select">
                    <Select value = {this.props.format} onChange={this.handleChange}>
                        <MenuItem value = 'hex'>HEX - #FFFFFF</MenuItem>
                        <MenuItem value='rgb'>RGB - rgb(255, 255, 255)</MenuItem>
                        <MenuItem value='rgba'>RGBA - rgba(255, 255, 255, 1.0)</MenuItem>
                    </Select>
                </div>
            </div>
        )
    }
}

export default PaletteNavbar;
