import React, { Component } from 'react';
import {Link} from 'react-router-dom'
import classNames from 'classnames'
import CssBaseline from '@material-ui/core/CssBaseline';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@icons/material/MenuIcon';
import Button from '@material-ui/core/Button';
import {Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle } from '@material-ui/core'
import { ValidatorForm, TextValidator} from 'react-material-ui-form-validator';


class FormNavbar extends Component{
    constructor(props){
        super(props);
        this.state = {
            openModal: false,
         
        }
        this.cancelSave = this.cancelSave.bind(this)
        this.openModal = this.openModal.bind(this)
        
    }

    openModal(){
        this.setState({
          openModal: true,
        })
      }
  
      cancelSave(){
        this.setState({
          openModal: false,
        })
      }


    render(){
        const {classes, open, paletteName} = this.props;
        const { openModal } = this.state;
   
        return(
            <div>
                <CssBaseline />
                <AppBar
                  color="default"
                    position="fixed"
                    className={classNames(classes.appBar, {
                        [classes.appBarShift]: open,
                    })}
                >
                    <Toolbar>
                        <IconButton
                            color="inherit"
                            aria-label="open drawer"
                            onClick={this.props.handleDrawerOpen}
                            edge="start"
                            className={classNames(classes.menuButton, open && classes.hide)}
                        >
                            <MenuIcon />
                        </IconButton>
                        <Typography variant="h6" noWrap>
                            Create A Palette
                        </Typography>
                        <div>
                          <Link className={classes.goBackLink} to = "/">
                            <Button variant="contained" color="secondary">
                                Go Back
                            </Button>
                          </Link>
                            
                            <Button variant="contained" color="primary" onClick={this.openModal}>
                                Save Palette
                            </Button>
                        </div>
                    </Toolbar>
                 
                </AppBar>

                <Dialog id="paletteNameModal" open={openModal} onClose={this.handleClose} aria-labelledby="form-dialog-title">
                  <DialogTitle id="form-dialog-title"> Add Palette Name</DialogTitle>

                  <DialogContent>
                      <DialogContentText>
                          Please Add Palette Name
                      </DialogContentText>

                    <ValidatorForm onSubmit={this.savePalette}>
                        <TextValidator 
                            value={paletteName}
                            onChange={this.handleChange}
                            name="paletteName"
                            validators={[
                                'required', 
                                'isPaletteNameUnique',
                              ]}
                            errorMessages={[
                              'This field is required. Enter a palette name!', 
                              'Palette name must be unique!', 
                            ]}

                            />
                          <DialogActions>
                            <Button onClick={this.cancelSave} color="primary">
                                Cancel
                            </Button>
                            <Button type="submit" color="primary">
                                Save
                            </Button>
                        </DialogActions>
                        {/* <Dialog open={openEmoji}>
                            <Picker  onSelect={this.addEmoji} />
                        </Dialog> */}

                    </ValidatorForm>
             
                  </DialogContent>
                       
                </Dialog>

            </div>

        )
    }
}

export default FormNavbar;
