import React from 'react'
import { Link } from 'react-router-dom'
import { withStyles } from '@material-ui/styles';
// import color from '@material-ui/core/colors/teal';
const styles = {
    root: {
        backgroundColor: "white",
        borderRadius: "5px",
        padding: "0.5rem",
        position: "relative",
        overflow: "hidden",
        "&:hover": {
            cursor: "pointer"
        }
    },
    colors: {
        backgroundColor: "gray",
        height: "150px",
        width: "100%",

    },
    color:{
        display: "inline-block",
        height: "25%",
        margin: "0 auto",
        // marginBottom: "-4px",
        position: "relative",
        width: "20%",
        verticalAlign: "middle",
        
    },
    title: {
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        margin: "0",
        color:"black",
        paddingTop: "0.5rem",
        fontSize: "1rem",
        position: "relative"

    },
    emoji: {
        marginLeft: "0.5rem",
        fontSize: "1.5rem",

    }
}

function MiniPalette (props) {
    const {classes, id, paletteName, colors, emoji} = props;

    // render(){
    //     const {paletteName, colors, id, emoji } = this.props;
        return(
            <div className={classes.root}>
                <div className={classes.colors}>
                    {colors.map(color => (
                        <div key={color.name} 
                            className={classes.color} style={{backgroundColor: color.color}}
                        >
                            {/* {color.color} */}
                        </div>
                    ))}
                </div>
                <h5 className={classes.title}>
                    <Link to = {`/palette/${id}`}>{paletteName}</Link>
                    <span className={classes.emoji}>{emoji}</span>
                </h5>
                {/* {props.paletteName} */}
                
               
            </div>
        )
    // }
    // {
    //     palette.colors.map(color =>(
    //         <div className="ColorBoxes" key={uuid()} style={{backgroundColor: color.color}}>
    //         </div>
    //     ))
    // }
}

export default withStyles(styles)(MiniPalette);
