import React, { Component } from 'react';
import {ChromePicker} from 'react-color'
import {Link} from 'react-router-dom'
import { ValidatorForm, TextValidator} from 'react-material-ui-form-validator';
import classNames from 'classnames'
import { withStyles } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import CssBaseline from '@material-ui/core/CssBaseline';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@icons/material/MenuIcon';
import ChevronLeftIcon from '@icons/material/ChevronLeftIcon';
import Button from '@material-ui/core/Button';
import {Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle } from '@material-ui/core'
import { Picker } from 'emoji-mart'
import DraggableColorBoxList from './DraggableColorBoxList'
import {arrayMove} from 'react-sortable-hoc';
import chroma from 'chroma-js'

class FormNavbar extends Component{
    constructor(props){
        super(props);
        this.state = {
            open: true,
            currentColor: "teal",
            newColorName: "",
            // colors: this.props.palettes[0].colors,
            paletteName:"",
            id:"",
            emoji:"",
            openModal: false,
            openEmoji: false,
        }
        
        this.handleColorPickerChange = this.handleColorPickerChange.bind(this)
        this.addNewColor = this.addNewColor.bind(this)
        this.savePalette = this.savePalette.bind(this)
        this.cancelSave = this.cancelSave.bind(this)
        this.openModal = this.openModal.bind(this)
        this.handleChange = this.handleChange.bind(this)
        this.deleteBox = this.deleteBox.bind(this)
        this.clearPalette = this.clearPalette.bind(this)
        this.chooseRandomColor = this.chooseRandomColor.bind(this);
    }

    render(){
        const {classes, maxColors} = this.props;
        const {open, currentColor, colors, newColorName, openModal, openEmoji, paletteName} = this.state;
        const paletteIsFull = colors.length >= maxColors;
        return(
            <div>
               <AppBar
                  color="default"
                    position="fixed"
                    className={classNames(classes.appBar, {
                        [classes.appBarShift]: open,
                    })}
                >
                    <Toolbar>
                        <IconButton
                            color="inherit"
                            aria-label="open drawer"
                            onClick={this.handleDrawerOpen}
                            edge="start"
                            className={classNames(classes.menuButton, open && classes.hide)}
                        >
                            <MenuIcon />
                        </IconButton>
                        <Typography variant="h6" noWrap>
                            Create A Palette
                        </Typography>
                        <div>
                          <Link className={classes.goBackLink} to = "/">
                            <Button variant="contained" color="secondary">
                                Go Back
                            </Button>
                          </Link>
                            
                            <Button variant="contained" color="primary" onClick={this.openModal}>
                                Save Palette
                            </Button>
                        </div>
                    </Toolbar>
                 
                </AppBar>

            </div>

        )
    }
}

export default FormNavbar;
