import React, { Component } from 'react'
import { withStyles } from '@material-ui/styles';
import styles from './styles/MiniPaletteStyles'
import IconButton from '@material-ui/core/IconButton';
import Button from '@material-ui/core/Button';
import {Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle } from '@material-ui/core'



class MiniPalette extends Component {
    constructor(props){
        super(props);
        this.state={
            open: false,
        }
        this.handleDelete = this.handleDelete.bind(this)
        this.handleClick = this.handleClick.bind(this)
    }

    handleDelete(evt){
        evt.stopPropagation()
        const id = evt.target.id;
        console.log(id)
        this.props.handleDelete(id)
    }
    handleClick(){
        this.setState({
            open: true,
        })
    }
    render(){
    const {classes, id, paletteName, colors, emoji, handleClick} = this.props;
    const {open} = this.state;

        return(
            <div className={classes.root} >
                <div className={classes.colors}>
                    {colors.map(color => (
                        <div key={color.name} 
                            className={classes.color} style={{backgroundColor: color.color}}
                        ></div>
                    ))}
                </div>
                <h5 className={classes.title} onClick={handleClick}>
                    {paletteName}
                    <span className={classes.emoji}>{emoji}</span>
                </h5> 
                <IconButton className ={classes.delete} id={id}  
                    // onClick={this.handleDelete}
                    onClick= {this.handleClick} 
                    
                >
                    <i id={id} className ="fas fa-trash"></i>
                </IconButton> 
               
                <Dialog className={classes.dialog}
                    open={open}
                    // TransitionComponent={Transition}
                    keepMounted
                    // onClose={handleClose}
                    aria-labelledby="alert-dialog-slide-title"
                    aria-describedby="alert-dialog-slide-description"
                >
                    
                    <span>X<Button > Cancel </Button></span>
                    <Button >
                    <i id={id} className ="fas fa-trash"></i>Delete
                    </Button>
                    
                  
                </Dialog>      
                
            </div>
        )
    }
}

export default withStyles(styles)(MiniPalette);
