import React, { Component } from 'react'
import {Link} from 'react-router-dom'
import Grid from '@material-ui/core/Grid';
import chroma from 'chroma-js'

class ColorBox extends Component {
    render(){
        let bgColor = this.props.colorCode; 
        let textColor = chroma.contrast(bgColor, 'white') >= 4.5 ? "white" : "black"
        return(
            <Grid className="ColorBox"  
                style={{backgroundColor: bgColor, color: textColor}}
            >
                {/* <div style={{backgroundColor: bgColor, display: "flex", height: "100%", color: textColor}}> */}
                    <div style={{alignSelf:"flex-end"}}>
                        {this.props.colorName} 
                    </div>
                   
                    <div>
                        {this.props.colorCode}
                    </div>
                    
                    {/* <Link to = {`/palette/${this.props.id}/${this.props.colorName}`} style={{alignSelf:"flex-end"}}>More</Link> */}
                    
                    <div style={{alignSelf: "center"}}>
                        Copy
                    </div> 
{/*                 
                </div> */}
            </Grid>
        )
    }
}

export default ColorBox;
