import mediaquery from './mediaquery'

export default {
    PaletteNavbar: {
        display: "flex",
        flexDirection:" row",
        fontFamily: "'Roboto', sans-serif",
        alignItems: "center",
        width: "70%",
        // [mediaquery.down('lg')]: {
        //     width: "25%",
        //     height: props => props.showingFullPalette? "20%" : "33.333%",
        // },
        [mediaquery.down('sm')]: {
            // flexDirection:" column",
            width: "100%"
        },
        [mediaquery.down('xs')]: {
            // height: "10vh",
            width: "100%"
        },
    },
    logo: {
        backgroundColor: "#ECEFF1",
        display: "flex",
        alignItems: "center",
        padding: "0 7px",
        textAlign: "left",
        height: "100%",
        "& a, a:hover":{
            color: "black",
            textDecoration: "none",
            fontWeight: '500',

        },
        // [mediaquery.down('lg')]: {
        //     width: "25%",
        //     height: props => props.showingFullPalette? "20%" : "33.333%",
        // },
        [mediaquery.down('sm')]: {
            display: 'none',
        },
        [mediaquery.down('xs')]: {
          display: 'none',
        },
    },

    sliderContainer: {
        width: "35%",
        margin: "0 auto",

        [mediaquery.down('xs')]: {
            width: "100%",
            textAlign: "left",
            paddingLeft: "2%"
        },

    },

    slider: {
        display: "inline-block",
        width: "65%",
        "& .rc-slider-track, .rc-slider-rail": {
            backgroundColor: "lightgray",
            height: "6px",
        },
        "& .rc-slider-track": {
            backgroundColor: "transparent",
        },
        "& .rc-slider-handle, .rc-slider-handle:active,.rc-slider-handle:focus, rc-slider-handle:hover": {
            backgroundColor: "green",
            outline: "none",
            border: "2px solid green",
            width: "16px",
            height:" 16px",
            boxShadow: "none",
        },
        [mediaquery.down('xs')]: {
            width: "100%",
            margin: "0 auto",
        },
    },

    sliderLegend: {
        display: "inline-block",
        width: "35%",
        [mediaquery.down('xs')]: {
            width: "50%",
            margin: "0 auto",
        },
    },

    select: {
        textAlign: "center",
        width: "45%",
        margin: "0 auto",
    },

    hidden: {
        opacity: "0"
    }

}
