import React, { Component } from 'react';
import {Link} from 'react-router-dom'
import ColorBox from './ColorBox'
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import {ChromePicker} from 'react-color'


class NewPaletteForm extends Component {
    static defaultProps = {
        maxColors: 20,
    }
    constructor(props){
        super(props);
        this.state = {
            paletteName: "",
            id: "",
            emoji: "",
            colors: [
                { name: "red", color: "#FF0000"}
            ],
            name: "",
            color: "",
            bgColor: "#FF0000",
          
        }
        this.handleChange = this.handleChange.bind(this)
        this.addColorName = this.addColorName.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleClick = this.handleClick.bind(this)

    }
    handleChange(color) {
        let newColor = color.hex
   
        this.setState({
            color:newColor, 
        })
    };

    addNewColor(color){
        this.setState({

        })
    }

    addColorName(evt){
    //    let name = evt.target.value
    //    let color = "blue"
    //    let newColor = {
    //        name: name,
    //        color: color
    //    }
    //    return newColor;
       
        this.setState({
            [evt.target.name]: evt.target.value, 
        })
        
    }

    handleSubmit(evt){
        evt.preventDefault();

    }

    handleClick(evt){
        let newColorName = this.state.name;
        let newColorCode = this.state.color;
        let toAdd = {
            name: newColorName,
            color: newColorCode
        }
        console.log(newColorName)
    
        this.setState(st => ({
            colors: [...st.colors,  toAdd],
            name: "",
            color: ""
        }))
    
    }

 
    render(){

 
        return(
            <Grid container justify="center" className = "NewPaletteForm" style={{height: "100vh"}}>
                <Grid container justify="center" style={{backgroundColor:"whitesmoke", width: "15%",  flexDirection: "column", alignItems: "center"}}>
                    <h2>Design Your Palette</h2>
                    <Grid item style= {{margin:"30px 0"}}>
                     
                            <Button variant="contained" color="primary">
                                Random Color
                            </Button>
                            <Button variant="contained" color="secondary">
                                Clear Palette
                            </Button>
                    
                    </Grid>
                    <Grid item>
                    
                        <ChromePicker color={this.state.colors.color}
                                    onChangeComplete = {this.handleChange}
                                    style={{margin: "30px auto"}}
                        />
                        <form onSubmit = {this.handleSubmit}>  
                            <TextField
                                id="filled-with-placeholder"
                                label="Color Name"
                                placeholder="Color Name"
                                margin="normal"
                                variant="filled"
                                value = {this.state.name}
                                name="name"
                                onChange={this.addColorName}
                            />
                            <Button onClick = {this.handleClick} style={{backgroundColor: this.state.colors.color}}>
                                Add Color
                            </Button>
                        </form>
                        
                      
                    </Grid>
                </Grid>
            
                <Grid container style={{width: "85%", flexDirection: "column" }}>
                    <Grid item style={{backgroundColor: "white", padding: "10px", height: "auto"}}>
                        <h5 style={{display: "inline-block", textAlign: "left", width: "50%" }}>Create A Palette</h5>
                        <div style={{display: "inline-block", textAlign: "right", width: "50%"}}>
                            <Button variant="contained" color="primary">
                                <Link to = "/" style={{color: "white"}}> Go Back</Link>
                            </Button>
                            <Button variant="contained" color="secondary">
                                Save
                            </Button>
                            
                        </div>
                    </Grid>
                    <Grid item> 
                        {this.state.colors.map(color => (
                            <ColorBox colorCode = {color.color}
                                    colorName = {color.name}
                            />
                        ))}
                        
                    </Grid>
                </Grid>
            </Grid>
         
        )
    }
}

export default NewPaletteForm;
