import React, { Component } from 'react';
import {Link} from 'react-router-dom'
import { withStyles } from '@material-ui/core/styles';
import classNames from 'classnames'
import CssBaseline from '@material-ui/core/CssBaseline';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@icons/material/MenuIcon';
import Button from '@material-ui/core/Button';
import {Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle } from '@material-ui/core'
import { ValidatorForm, TextValidator} from 'react-material-ui-form-validator';
import { Picker } from 'emoji-mart'
import 'emoji-mart/css/emoji-mart.css'

class ModalDialog extends Component {
    constructor(props){
        super(props);
        this.state = {
            paletteName: "",
            emoji:"",
            openEmoji: true,
        }

        this.handleChange = this.handleChange.bind(this)
        this.addEmoji = this.addEmoji.bind(this)
        
    }

    componentDidMount() {
        // validate if the palette name is unique
        ValidatorForm.addValidationRule('isPaletteNameUnique', value =>       
            this.props.palettes.every(({ paletteName } ) => paletteName.toLocaleLowerCase() !== value.toLocaleLowerCase())
        );
    
    }

    handleChange(evt){
        this.setState({
          [evt.target.name]: evt.target.value,
        })
    }

    addEmoji(evt){
        // this.setState({
        //     emoji: evt.target.emoji,
        // })
        console.log(evt)
    }

    render(){
        const {paletteName, openEmoji} = this.state;
        const {openModal, cancelSave} = this.props;
        return(
            <div>
                <Dialog id="paletteNameModal" open={openModal} onClose={cancelSave} aria-labelledby="form-dialog-title">
                    <DialogTitle id="form-dialog-title"> Add Palette Name</DialogTitle>

                    <DialogContent>
                        
                        <DialogContentText>
                            Please Add Palette Name
                        </DialogContentText>

                        

                        <ValidatorForm onSubmit={() => this.props.savePalette(paletteName)}>
                            <TextValidator 
                                value={paletteName}
                                onChange={this.handleChange}
                                name="paletteName"
                                validators={[
                                    'required', 
                                    'isPaletteNameUnique',
                                ]}
                                errorMessages={[
                                'This field is required. Enter a palette name!', 
                                'Palette name must be unique!', 
                                ]}

                                />
                            <DialogActions>
                                <Button onClick={cancelSave} color="primary">
                                    Cancel
                                </Button>
                                <Button type="submit" color="primary">
                                    Save
                                </Button>
                            </DialogActions>
                        </ValidatorForm>
                       
                    </DialogContent>
                       
                </Dialog>

                <Dialog open={openEmoji}>
                    <Picker  
                        onSelect = {this.addEmoji} 

                    />  
                </Dialog>

            </div>
        )
    }
}

export default ModalDialog;
