import React, {Component} from 'react'
import Slider from 'rc-slider';
import {Link} from 'react-router-dom'
import Snackbar from '@material-ui/core/Snackbar';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@icons/material/CloseIcon';
import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';
import { withStyles } from '@material-ui/styles';
import 'rc-slider/assets/index.css'
import './PaletteNavbar.css'

const styles = {
    PaletteNavbar: {
        display: "flex",
        flexDirection:" row",
        fontFamily: "'Roboto', sans-serif",
        alignItems: "center",
        width: "70%",
    },
    logo: {
        backgroundColor: "#ECEFF1",
        display: "flex",
        alignItems: "center",
        padding: "0 7px",
        textAlign: "left",
        height: "100%",
        "& a, a:hover":{
            color: "black",
            textDecoration: "none",
            fontWeight: '500',

        }
    },
    sliderContainer: {
        width: "35%",
        margin: "0 auto",

    },

    slider: {
        display: "inline-block",
        width: "65%",
    },

    sliderLegend: {
        display: "inline-block",
        width: "35%",
    },
    select: {
        textAlign: "center",
        width: "45%",
        margin: "0 auto",
    }

}

class PaletteNavbar extends Component {

    constructor(props){
        super(props);
        this.state={
            value: 500,
            open: false,
        }
          
        this.handleFormatChange = this.handleFormatChange.bind(this)
        this.handleShadeChange = this.handleShadeChange.bind(this)
        this.closeSnackbar = this.closeSnackbar.bind(this)
    }

    handleFormatChange(evt){
        this.props.handleFormat(evt.target.value)
        this.setState({
            open: true,
        })
    }

    handleShadeChange(value){
        this.props.changeShade(value);
        this.setState({
            value: value,
        })
        
    }
    closeSnackbar(){
        this.setState({open: false})
    }

    render(){
        const {value, open} = this.state;
        const {format, classes} = this.props;
        return(
            <div className={classes.PaletteNavbar}>
                <div className={classes.logo}>
                    <h3>
                        <Link to="/">ColorPalettes</Link>
                    </h3>
                </div>
              
                    <div className={classes.sliderContainer}>
                        <span className={classes.sliderLegend}>
                            Level: [{value}]
                        </span>
                        
                            <div className={classes.slider}>
                                <Slider 
                                    defaultValue={value}
                                    step={100}
                                    min={100}
                                    max={900}
                                    onChange={this.handleShadeChange}
                                />
                            </div>
                    
                    </div>
                
                <div className={classes.select}>
                    <Select value = {format} onChange={this.handleFormatChange}>
                        <MenuItem value = 'hex'>HEX - #FFFFFF</MenuItem>
                        <MenuItem value='rgb'>RGB - rgb(255, 255, 255)</MenuItem>
                        <MenuItem value='rgba'>RGBA - rgba(255, 255, 255, 1.0)</MenuItem>
                    </Select>
                </div>
                
                <Snackbar
                    anchorOrigin={{
                        vertical: 'bottom',
                        horizontal: 'left',
                    }}
                    open={open}
                    autoHideDuration={3000}
                    message={<span id="message-id">Format Changed To {format.toUpperCase()}!</span>}
                    ContentProps={{
                        'aria-describedby': 'message-id',
                    }}
                    onClose={this.closeSnackbar}
                    
                    action={[
                    <IconButton
                        key="close"
                        aria-label="close"
                        color="inherit"
                        // className={classes.close}
                        onClick={this.closeSnackbar}
                    >
                        <CloseIcon />
                    </IconButton>,
                    ]}
                />
            </div>
        )
    }
}

export default withStyles(styles) (PaletteNavbar);
