import React, { Component } from 'react';
import {Link} from 'react-router-dom'
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import ButtonGroup from '@material-ui/core/ButtonGroup';
import TextField from '@material-ui/core/TextField';
import {ChromePicker} from 'react-color'


class NewPaletteForm extends Component {
    constructor(props){
        super(props);
        this.state = {
            bgColor: "red",
          
        }
        this.handleChange = this.handleChange.bind(this)

    }
    handleChange(color) {
        this.setState({  bgColor: color.hex });
      };

   
    render(){

 
        return(
            <Grid container justify="center" className = "NewPaletteForm" style={{height: "100vh"}}>
                <Grid container justify="center" style={{backgroundColor:"white", width: "10%", flexDirection: "column", alignItems: "center" }}>
                    <Grid item style= {{margin:"30px 0"}}>
                     
                            <Button variant="contained" color="primary">
                                Random Color
                            </Button>
                            <Button variant="contained" color="secondary">
                                Clear Palette
                            </Button>
                    
                    </Grid>
                    <Grid item>
                
                        <ChromePicker color={this.state.bgColor}
                                    onChange = {this.handleChange}
                                    style={{margin: "30px auto"}}
                        />
                        <form>
                            <TextField
                                id="filled-with-placeholder"
                                label="Color Name"
                                placeholder="Color Name"
                                margin="normal"
                                variant="filled"
                            />
                        </form>
                        <Button style={{backgroundColor: this.state.bgColor}}>
                            Add Color
                        </Button>
                      
                    </Grid>
                </Grid>
            
                <Grid container style={{width: "90%"}}>
                    <Grid item>
                        <div>
                            <button>Save</button>
                            <Link to = "/"> Go Back</Link>
                        </div>
                    
                        
                        <div style={{backgroundColor: this.state.bgColor, height: "200px"}}>
                            To change background color
                        </div>
                    </Grid>
                </Grid>
            </Grid>
         
        )
    }
}

export default NewPaletteForm;
