import React, { Component } from 'react';
import {Link} from 'react-router-dom'
import {ChromePicker} from 'react-color'
import ColorBox from './ColorBox'
import seedColors from './seedColors'
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
// import DeleteIcon from '@icons/material/DeleteIcon';
// import { Card, Container, IconButton } from '@material-ui/core';



class NewPaletteForm extends Component {
    static defaultProps = {
        maxColors: 20,
    }
    constructor(props){
        super(props);
        this.state = {
    
            paletteName: "",
            id: "",
            emoji: "",
            colors: [
                { name: "red", color: "#FF0000"},
                { name: "red", color: "#FF0000"}
            ],
        
            name: "",
            color: "",
            bgColor: "#FF0000",
            open: false,
          
        }
        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleClick = this.handleClick.bind(this)
        this.handleClickOpen = this.handleClickOpen.bind(this)
        this.handleClose = this.handleClose.bind(this)
        this.handleSave = this.handleSave.bind(this)
        this.paletteName = this.paletteName.bind(this)
        this.addColorName = this.addColorName.bind(this)
        this.addPaletteToPaletteList = this.addPaletteToPaletteList.bind(this)

    }

    // adding the palette to paletteList
    addPaletteToPaletteList(){
        const {paletteName, emoji, colors} = this.state;
        let id = paletteName.toLocaleLowerCase().split(' ').join("-");
        let palettes = seedColors;
        let newPalette = {
            paletteName: paletteName,
            id: id,
            emoji: emoji,
            colors: colors
        }
        palettes.push(newPalette)
    }


    handleChange(color) {
        let newColor = color.hex
   
        this.setState({
            color:newColor, 
        })
    };

  
    addColorName(evt){
       
        this.setState({
            [evt.target.name]: evt.target.value, 
        })
        
    }

    handleSubmit(evt){
        evt.preventDefault();

    }

    handleClick(evt){
        let newColorName = this.state.name;
        let newColorCode = this.state.color;
        let toAdd = {
            name: newColorName,
            color: newColorCode
        }
    
        this.setState(st => ({
            
            colors: [...st.colors,  toAdd],
            name: "",
            color: ""
        }))
    
    }
   
// opens modal form for palette name
    handleClickOpen() {
        this.setState({
            open: true,
        })
    
    }

// close modal form
    handleClose(evt) {
        this.setState({
            open:false,
        })
    }

// when save is clicked, palette is added to the palette list
    handleSave(){
        this.addPaletteToPaletteList();
        this.setState({
            open:false,
        })
    }
// set the palette name with modal form
    paletteName(evt){
        this.setState({
            paletteName: evt.target.value, 
        })
    }
 
    render(){
        return(
            <div className = "NewPaletteForm" style={{minHeight: "100vh"}}>
                <div style={{backgroundColor:"whitesmoke", width: "20vw",  flexDirection: "column", alignItems: "center"}}>
                    <h2>Design Your Palette</h2>
                    <div item style= {{margin:"30px 0"}}>
                     
                        <Button variant="contained" color="primary">
                            Random Color
                        </Button>
                        <Button variant="contained" color="secondary">
                            Clear Palette
                        </Button>
                    
                    </div>
                    <Grid item>
                    
                        <ChromePicker color={this.state.color}
                                    onChangeComplete = {this.handleChange}
                        />
                        <form onSubmit = {this.handleSubmit}>  
                            <TextField
                                id="filled-with-placeholder"
                                label="Color Name"
                                placeholder="Color Name"
                                margin="normal"
                                variant="filled"
                                value = {this.state.name}
                                name="name"
                                onChange={this.addColorName}
                            />
                            <Button onClick = {this.handleClick} style={{backgroundColor: this.state.color}}>
                                Add Color
                            </Button>
                        </form>
                    </Grid>
                </div>
            
                <Grid container style={{width: "80vw", flexDirection: "column" }}>
                    <Grid item style={{backgroundColor: "white", padding: "10px", height: "auto"}}>
                        <h5 style={{display: "inline-block", textAlign: "left", width: "50%" }}>Create A Palette</h5>
                        <div style={{display: "inline-block", textAlign: "right", width: "50%"}}>
                            <Button variant="contained" color="primary">
                                <Link to = "/" style={{color: "white"}}> Go Back</Link>
                            </Button>
                            
                                <Button variant="contained" color="secondary" onClick={this.handleClickOpen}>
                                    Save
                                </Button>
                                <Dialog open={this.state.open} onClose={this.handleClose} aria-labelledby="form-dialog-title">
                                    <DialogTitle id="form-dialog-title"> Add Palette Name</DialogTitle>

                                    <DialogContent>
                                        <DialogContentText>
                                           Please Add Palette Name
                                        </DialogContentText>

                                        <TextField
                                            autoFocus
                                            margin="dense"
                                            id="name"
                                            value={this.state.paletteName}
                                            label="Palette Name"
                                            type="name"
                                            fullWidth
                                            onChange = {this.paletteName}
                                        />
                                    </DialogContent>

                                    <DialogActions>
                                        <Button onClick={this.handleClose} color="primary">
                                            Cancel
                                        </Button>
                                        <Button onClick={this.handleSave } color="primary">
                                            Save
                                        </Button>
                                    </DialogActions>
                                </Dialog>
                        </div>
                    </Grid> 

                    <Grid container> 
                        {this.state.colors.map(color => (
                            
                                <ColorBox bgColor = {color.color}
                                        colorName = {color.name}
                                />
                              
                        ))}
                        
                    </Grid>
                </Grid>

               
            </div>
         
        )
    }
}

export default NewPaletteForm;
