import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { withStyles } from '@material-ui/styles';
import styles from './styles/ColorPaletteListStyles';
import MiniPalette from './MiniPalette'
import Button from '@material-ui/core/Button';
import CancelIcon from '@icons/material/CancelIcon'
import CheckIcon from '@icons/material/CheckIcon'
import {Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle } from '@material-ui/core'


class ColorPaletteList extends Component {
    constructor(props){
        super(props);
        this.state={
            open: false,
        }
        this.handleDelete = this.handleDelete.bind(this)
    }

    handleDelete(id){
        this.props.removePalette(id)
    }

    goToPalette(id){
        this.props.history.push(`/palette/${id}`);
    }


    render() {
        const {colorPalettes, classes, id} = this.props;
        const {open} = this.state;
        return(
            <div className={classes.root}>
                <div className = {classes.container}>
                    <nav className={classes.navbar}>
                        <h1 className={classes.title}>Color Palettes</h1>
                        <h5 className={classes.link}>
                            <Link to ="/palette/new">Create A New Palette</Link>
                        </h5>
                    </nav>
                    <div className={classes.palettes}>
                        {colorPalettes.map(palette => ( 
                            <div key={palette.id}>
                                <MiniPalette 
                                    id={palette.id}
                                    // key={palette.id}
                                    colors={palette.colors}
                                    paletteName={palette.paletteName}
                                    emoji = {palette.emoji}
                                    handleClick = {() => this.goToPalette(palette.id)}
                                    handleDelete={this.handleDelete}
                                />
                            </div>
                        ))}
                        
                    </div>

                    <Dialog className={classes.dialog}
                    open={open}
                    // TransitionComponent={Transition}
                    keepMounted
                    // onClose={handleClose}
                    aria-labelledby="alert-dialog-slide-title"
                    aria-describedby="alert-dialog-slide-description"
                >
                <DialogTitle id="form-dialog-title"> Delete This Palette?</DialogTitle>
                    <CancelIcon />
                    {/* <span>X<Button > Cancel </Button></span> */}
                    <CheckIcon> Delete </CheckIcon>
                    <Button >
                    <i id={id} className ="fas fa-trash"></i>Delete
                    </Button>
                    
                  
                </Dialog> 

                </div>
            </div>

        )
    }
}

export default withStyles(styles)(ColorPaletteList);
