import React, { Component } from 'react'
import {Link} from 'react-router-dom'

class ColorBox extends Component {
    render(){
        return(
            <div className="ColorBox">
                <div style={{backgroundColor: this.props.colorCode, display: "flex", height: "20vh"}}>
                    <div style={{alignSelf:"flex-end"}}>{this.props.colorName} </div>
                    <div>{this.props.colorCode}</div>
                     <Link to = {`/palette/${this.props.id}/${this.props.colorName}`} style={{alignSelf:"flex-end"}}>More</Link>
                    <div style={{alignSelf: "center"}}>Copy</div> 
                
                </div>
            </div>
        )
    }
}

export default ColorBox;
