var rgbToHex = function (rgb) { 
    var hex = Number(rgb).toString(16);
    if (hex.length < 2) {
         hex = "0" + hex;
    }
    return hex;
  };

  var fullColorHex = function(r,g,b) {   
    var red = rgbToHex(r);
    var green = rgbToHex(g);
    var blue = rgbToHex(b);
    return red+green+blue;
  };

  var colorShades = function(r, g, b){
      var red = rgbToHex(Math.floor(5*r/10))
      var green = rgbToHex(Math.floor(5*g/10))
      var blue = rgbToHex(Math.floor(5*b/10))
      return red+green+blue;
  }

  export {fullColorHex, colorShades}
