import React, { Component } from 'react'
import {Link} from 'react-router-dom'
import {CopyToClipboard} from 'react-copy-to-clipboard'
// import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import chroma from 'chroma-js'
import { withStyles } from '@material-ui/styles';
import './ColorBox.css'

const styles = {

    ColorBox: {
        display: "inline-block",
        height: props => props.showingFullPalette? "25%" : "50%",
        margin: "0 auto",
        position: "relative",
        width: "20%",
        verticalAlign: "middle",
        "&:hover button:first-of-type": {
            opacity: "1",
            transition: "0.5s"
        }
    },
    dynamicColor:{
        color: props => chroma.contrast(props.bgColor, 'white') >= 4.5 ? "white" : "black",
        backgroundColor: props => props.bgColor,
    },

    show: {
        opacity: "1",
        position: "absolute",
        transform: "scale(50)",
        zIndex: "10",
    },

    copyButton: {
        color: props => chroma.contrast(props.bgColor, 'white') >= 4.5 ? "white" : "black",
        backgroundColor: "rgba(255, 255, 255, 0.3)!important",
        display: "inline-block",
        fontSize:" 1rem",
        height:" 30px",
        marginTop: "-15px",
        marginLeft: "-50px",
        left: "50%",
        letterSpacing: "1px",
        opacity: "0",
        border:" none",
        position: "absolute",
        textAlign: "center",
        textTransform: "uppercase",
        top: "50%",
        width: "100px",
    },
    moreButton: {
        color: props => chroma.contrast(props.bgColor, 'white') >= 4.5 ? "white" : "black",
        backgroundColor: "rgba(255, 255, 255, 0.3)!important",
        bottom: "0",
        borderRadius: "0!important",
        fontSize: "12px!important",
        letterSpacing: "1px!important",
        opacity: "1",
        position:" absolute!important",
        right: "0",
        textTransform: "uppercase",
        "& a:hover": {
            textDecoration: "none!important"
        }
    }

}

class ColorBox extends Component {
    constructor(props){
        super(props);
        this.state = {
            copied: false,
        }
        this.handleCopy = this.handleCopy.bind(this)
        // this.handleClick = this.handleClick.bind(this);
    }

    handleCopy(evt){
    //    this.props.onCopy(true)
       this.setState({
           copied: true,
        },() => (setTimeout(() => this.setState({copied: false}), 1500)))
    }

    // handleClick(evt){
    //     this.setState({
    //         copied: true,
    //     })
    //     setTimeout(()=>
    //         this.setState({
    //             copied: false,
    //     }),1000)
    // }
    render(){
        // let bgColor = this.props.colorCode; 
        const { bgColor, colorName, paletteId, id, showingFullPalette, classes, format } = this.props;
        const{copied } = this.state;
        const textColor = chroma.contrast(bgColor, 'white') >= 4.5 ? "white" : "black"
        return(
            
            <div className={`${classes.ColorBox} ${classes.dynamicColor}`}>
                <div className={copied ? `ColorBox-CopyOverlay ${classes.dynamicColor} ${classes.show}` : `ColorBox-CopyOverlay ${classes.dynamicColor}`} 
                    
                ></div>
                <div className={`ColorBox-OverlayerMsg ${copied && 'show'}`}>
                    <h1>Copied!</h1>
                    <p>{bgColor}</p>
                </div>

                <div className="ColorBox-ColorName">
                    {colorName} 
                </div>
                
                <CopyToClipboard onCopy = {this.handleCopy} 
                    text={(format === 'hex' &&  chroma(bgColor).hex()) ||
                        (format === "rgb" && chroma(bgColor).css()) ||
                        (format === "rgba" && chroma(bgColor).alpha(0.9).css()) }
                >
                    <button className={classes.copyButton}>
                        Copy
                    </button>
                </CopyToClipboard>
           
            {showingFullPalette &&
                <Button className={classes.moreButton}>
                    <Link  to = {`/palette/${paletteId}/${id}`} 
                            onClick={e => e.stopPropagation()} style={{ color: textColor}}>
                        More
                    </Link> 
                </Button>
            }
               
                
            </div>
            
        )
    }
}

export default withStyles(styles)(ColorBox);
