import React, { Component } from 'react'
import {ChromePicker} from 'react-color'
import { ValidatorForm, TextValidator} from 'react-material-ui-form-validator';
import {arrayMove} from 'react-sortable-hoc';
import classNames from 'classnames'
import { withStyles } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import ChevronLeftIcon from '@icons/material/ChevronLeftIcon';
import Button from '@material-ui/core/Button';
import DraggableColorBoxList from './DraggableColorBoxList'
import Typography from '@material-ui/core/Typography';
import chroma from 'chroma-js'
import FormNavbar from './FormNavbar'
import ColorPicker from './ColorPicker'



const drawerWidth = 400;

const styles = theme => ({
  root: {
    display: 'flex',
  },
  appBar: {
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShift: {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: drawerWidth,
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  hide: {
    display: 'none',
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
  },
  drawerHeader: {
    display: 'flex',
    alignItems: 'center',
    padding: theme.spacing(0, 1),
    ...theme.mixins.toolbar,
    justifyContent: 'flex-end',
  },
  content: {
    flexGrow: 1,
    height: "calc(100vh - 64px)",
    padding: theme.spacing(0),
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: -drawerWidth,
  },
  contentShift: {
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: 0,
  },

  goBackLink: {
    "&:hover":{
      textDecoration: "none"
    }

  }
    

});

class MakeNewPalette extends Component {
    static defaultProps = {
      maxColors: 20,
    }
    constructor(props){
        super(props);
        this.state = {
            open: true,
            currentColor: "teal",
            newColorName: "",
            colors: this.props.palettes[0].colors,
            paletteName:"",
            id:"",
            emoji:"",
            openEmoji: false,
        }

        this.colorsSet = new Set(this.state.colors.map(colors => colors.color))
        
        this.handleColorPickerChange = this.handleColorPickerChange.bind(this)
        this.addNewColor = this.addNewColor.bind(this)
        this.savePalette = this.savePalette.bind(this)
        this.handleChange = this.handleChange.bind(this)
        this.deleteBox = this.deleteBox.bind(this)
        this.clearPalette = this.clearPalette.bind(this)
        this.chooseRandomColor = this.chooseRandomColor.bind(this);
    }

    componentDidMount() {
    //   // validate if the color name is unique
    //   ValidatorForm.addValidationRule('isColorNameUnique', value => 
    //     this.state.colors.every(({ name }) => name.toLowerCase() !== value.toLowerCase())
    //   );

    // //  validate if the color is unique
    //   ValidatorForm.addValidationRule('isColorUnique', value => 
    //     this.state.colors.every(({ color }) => color !== this.state.currentColor)
    //   );

      // validate if the palette name is unique
      ValidatorForm.addValidationRule('isPaletteNameUnique', value =>       
        this.props.palettes.every(({ paletteName } ) => paletteName.toLocaleLowerCase() !== value.toLocaleLowerCase())
      );

    }
  
    handleDrawerOpen = () => {
        this.setState({open: true});
      };

    handleDrawerClose = () => {
      this.setState({open: false});
    };

    handleColorPickerChange(newColor){
        this.setState({
            currentColor: newColor.hex,
        })
    }

    addNewColor(newColorObj){
    
      // const newColorObj = {
      //   name: this.state.newColorName,
      //   color: this.state.currentColor,
      // }
      this.setState({
        colors: [...this.state.colors, newColorObj],
      });
    }

 
    savePalette(){
      const{paletteName, emoji, colors} = this.state;
      let id = this.state.paletteName.toLocaleLowerCase().split(' ').join('-');
      const newPalette = {
        paletteName: paletteName,
        id: id,
        emoji: emoji,
        colors: colors,
      }
      this.setState({
        openEmoji: true,
        openModal: false,
      })
      // console.log(newPalette)
      this.props.savePalette(newPalette)
      this.props.history.push("/");
     
    }

    // openModal(){
    //   this.setState({
    //     openModal: true,
    //   })
    // }

    // cancelSave(){
    //   this.setState({
    //     openModal: false,
    //   })
    // }

    handleChange(evt){
      this.setState({
        [evt.target.name]: evt.target.value,
      })
    }

    // addEmoji(emoji){
    //   let newEmoji = emoji.native;
    //   this.setState({
    //     emoji: newEmoji,
    //     openEmoji: false,
    //   })
      
    // }

    // handleSubmit(emoji){
    //   this.addEmoji(emoji)
    // }
    deleteBox(name){
      const oldColorsArray = [...this.state.colors]
      const updatedColorsArray = oldColorsArray.filter(box => box.name !== name);
  
      this.setState({
        colors: updatedColorsArray,
      })
    }

    onSortEnd = ({oldIndex, newIndex}) => {
      this.setState(({colors}) => ({
        colors: arrayMove(colors, oldIndex, newIndex),
      }));
    };

    clearPalette(){
      this.setState({
        colors: [],
      })
    }

    chooseRandomColor(){
      const colors = this.props.palettes.map(palette => palette.colors).flat();
      const random = Math.floor(Math.random() * colors.length)
      let newRandomColor = colors[random]
      let colorsSet = new Set(this.state.colors.map(color => color.color))

      if(!colorsSet.has(newRandomColor.color)){
        this.setState({
          colors: [...this.state.colors, newRandomColor],
        })
        colorsSet.add(newRandomColor)
      } else if(this.colorsSet.has(newRandomColor.color)){
        alert(newRandomColor.name + " already exists! Please choose again!")
      }
       
    }

    render(){
        const {classes, maxColors, palettes} = this.props;
        const {open, currentColor, colors, newColorName, openModal, openEmoji, paletteName} = this.state;
        const paletteIsFull = colors.length >= maxColors;

        return(
            <div className={classes.root} >
                
                <FormNavbar 
                  open = {open}
                  classes={classes}
                  handleDrawerOpen = {this.handleDrawerOpen}
                  handleChange = {this.handleChange}
                  savePalette = {this.savePalette}
                  paletteName={paletteName}
                />
                {/* <AppBar
                  color="default"
                    position="fixed"
                    className={classNames(classes.appBar, {
                        [classes.appBarShift]: open,
                    })}
                >
                    <Toolbar>
                        <IconButton
                            color="inherit"
                            aria-label="open drawer"
                            onClick={this.handleDrawerOpen}
                            edge="start"
                            className={classNames(classes.menuButton, open && classes.hide)}
                        >
                            <MenuIcon />
                        </IconButton>
                        <Typography variant="h6" noWrap>
                            Create A Palette
                        </Typography>
                        <div>
                          <Link className={classes.goBackLink} to = "/">
                            <Button variant="contained" color="secondary">
                                Go Back
                            </Button>
                          </Link>
                            
                            <Button variant="contained" color="primary" onClick={this.openModal}>
                                Save Palette
                            </Button>
                        </div>
                    </Toolbar>
                 
                </AppBar> */}
                <Drawer
                  className={classes.drawer}
                  variant="persistent"
                  anchor="left"
                  open={open}
                  classes={{
                      paper: classes.drawerPaper,
                  }}
                >
                  <div className={classes.drawerHeader}>
                      <IconButton onClick={this.handleDrawerClose}>
                            <ChevronLeftIcon /> 
                      </IconButton>
                  </div>

                  <Divider />

                  <Typography variant="h4" noWrap>
                      Design Your Palette
                  </Typography>

                  <div>
                    <Button 
                        variant="contained" 
                        color="primary" 
                        onClick={this.chooseRandomColor}
                        disabled ={paletteIsFull}
                      >
                        Random Color
                    </Button>
                    <Button variant="contained" color="secondary" onClick={this.clearPalette}>
                        Clear Palette
                    </Button>
                  </div>

                  <ColorPicker 
                    currentColor = {currentColor}
                    newColorName = {newColorName}
                    paletteIsFull = {paletteIsFull}
                    handleChange = {this.handleChange}
                    addNewColor = {this.addNewColor}
                    handleColorPickerChange = {this.handleColorPickerChange}
                    palettes = {palettes}
                  />
                  
                  {/* <ChromePicker 
                    color={currentColor} 
                    onChangeComplete = {this.handleColorPickerChange}
                  />


                  <ValidatorForm 
                    onSubmit={this.addNewColor}
                  
                  >
                    <TextValidator 
                      value={newColorName}
                      onChange={this.handleChange}
                      name="newColorName"
                      validators={[
                          'required', 
                          'isColorNameUnique', 
                          'isColorUnique'
                          ]}
                      errorMessages={[
                          'This field is required. Enter a color name!', 
                          'Color name must be unique!', 
                          'Color already exists!'
                          ]}
                    />
                  { paletteIsFull ? 
                      <Button 
                        type="submit"
                        variant="contained" 
                        disabled
                      >
                        Palette Full
                      </Button>
                  :
                      <Button 
                        type="submit"
                        variant="contained" 
                        style={{backgroundColor: currentColor}}
                        disabled ={paletteIsFull}
                      >
                        Add Color
                    </Button>
                  }    
                                    
                  </ValidatorForm> */}
                </Drawer>

                <main className={classNames(classes.content, {
                        [classes.contentShift]: open,
                    })}
                >
                <div className={classes.drawerHeader} />
                      
                <DraggableColorBoxList 
                  colors={colors} 
                  deleteBox={this.deleteBox} 
                  axis="xy"
                  onSortEnd={this.onSortEnd}
                />                                
                </main>
          </div>
        )
    }
}

export default withStyles(styles, {withTheme: true}) (MakeNewPalette);
