import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import PaletteNavbar from './PaletteNavbar'
import ColorBox from './ColorBox'
import Grid from '@material-ui/core/Grid';
import chroma from 'chroma-js'

class PaletteShades extends Component {

    render(){
        let shades = [];
        let baseColor = this.props.color.color;
        let darker = [1,1.5,2,2.5,3,4]
        let makeShades = [3, 2.5, 2, 1.5, 1, 0.5, 0] 

        for(let i = 0; i < makeShades.length; i++){
            let colorShade = chroma(baseColor).brighten(makeShades[i]).hex()
            console.log(colorShade)
            shades.push(colorShade)
            console.log(shades)
        }

        for(let i = 0; i < darker.length; i++){
            let colorShade = chroma(baseColor).darken(darker[i]).hex()
            console.log(colorShade)
            shades.push(colorShade)
            console.log(shades)
        }
        
       
        return(
            <div>
                <PaletteNavbar handleFormat = {this.props.handleFormat} format = {this.props.format}/>
               
                <Link to = {`/palette/${this.props.palette.id}`}>Go back</Link>

                {
                    shades.map(bgColor => (
                        <div style={{backgroundColor: bgColor, height: "100px", 
                        color: chroma.contrast(bgColor, 'white') >= 4.5 ? "white" : "black"
                        }}>
                            {bgColor}
                        </div>
                    ))
                } 

                   <Grid container spacing={0}>
                     <Link to = "/palette">Go back</Link>
                    {
                        shades.map(bgColor => (
                            <Grid item xs={12} sm={3} >
                                <ColorBox  
                                
                         
                                    colorCode = {
                                        (this.props.format === 'hex' &&  chroma(bgColor).hex()) ||
                                        (this.props.format === "rgb" && chroma(bgColor).css()) ||
                                        (this.props.format === "rgba" && chroma(bgColor).alpha(0.9).css())                                                             
                                    }
                                                                                       
                                />
                            </Grid>
                        ))
                    }
                </Grid>        
                
               

            </div>
        )
    }
}

export default PaletteShades;
