import React, { Component } from 'react'

class ColorBox extends Component {
    render(){
        return(
            <div className="ColorBox">
                <div style={{backgroundColor: this.props.colorCode, height: "25vh", position: "relative"}}>
                    {this.props.colorName} 
                    {this.props.colorCode}
                    <div style={{position: "absolute", bottom: "0", right: "0"}}>More</div>
                    <div style={{alignSelf: "center"}}>Copy</div>
                
                </div>
            </div>
        )
    }
}

export default ColorBox;
