import React, { Component } from 'react'
import {Link} from 'react-router-dom'
import {CopyToClipboard} from 'react-copy-to-clipboard'
// import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import chroma from 'chroma-js'
import { withStyles } from '@material-ui/styles';
import './ColorBox.css'

const styles = {
    textColor:{
        color: props => chroma.contrast(props.bgColor, 'white') >= 4.5 ? "white" : "black",
        backgroundColor: props => props.bgColor,
    },

    show: {
        opacity: "1",
        position: "absolute",
        transform: "scale(50)",
        zIndex: "10",
    },

    showMsg: {
        opacity: "1",
        transition: "all 0.4s ease-in-out",
        transitionDelay: "0.3s",
        transform: "scale(1)",
        zIndex: "15"
    },

    // OverlayerMsg: {
    //     alignItems: "center",
    //     bottom: "0",
    //     display: "flex",
    //     flexDirection: "column",
    //     left: "0",
    //     justifyContent: "center",
    //     position: "fixed",
    //     top: "0",
    //     right:" 0",
    //     opacity: "0",
    //     transform: "scale(0.1)",
    // }


}

class ColorBox extends Component {
    constructor(props){
        super(props);
        this.state = {
            copied: false,
        }
        this.handleCopy = this.handleCopy.bind(this)
        // this.handleClick = this.handleClick.bind(this);
    }

    handleCopy(evt){
    //    this.props.onCopy(true)
       this.setState({
           copied: true,
        },() => (setTimeout(() => this.setState({copied: false}), 1500)))
    }

    // handleClick(evt){
    //     this.setState({
    //         copied: true,
    //     })
    //     setTimeout(()=>
    //         this.setState({
    //             copied: false,
    //     }),1000)
    // }
    render(){
        // let bgColor = this.props.colorCode; 
        const { bgColor, colorName, paletteId, id, showLink, classes, format } = this.props;
        const{copied } = this.state;
        const textColor = chroma.contrast(bgColor, 'white') >= 4.5 ? "white" : "black"
        return(
            
            <div className={`ColorBox ${classes.textColor}`}>
                <div className={copied ? `ColorBox-CopyOverlay ${classes.textColor} ${classes.show}` : `ColorBox-CopyOverlay  ${classes.textColor}`} 
                    style={{backgroundColor: bgColor, color: textColor}}
                ></div>
                <div className={`ColorBox-OverlayerMsg ${this.state.copied && 'show'}`}>
                    <h1>Copied!</h1>
                    <p>{bgColor}</p>
                </div>

                <div className="ColorBox-ColorName">
                    {colorName} 
                </div>
                
                <CopyToClipboard onCopy = {this.handleCopy} 
                    text={(format === 'hex' &&  chroma(bgColor).hex()) ||
                        (format === "rgb" && chroma(bgColor).css()) ||
                        (format === "rgba" && chroma(bgColor).alpha(0.9).css()) }
                >
                    <button  className="ColorBox-CopyButton" style={{ color: textColor }}>
                        Copy
                    </button>
                </CopyToClipboard>
           
            {showLink &&
                <Button className="ColorBox-MoreButton">
                    <Link  to = {`/palette/${paletteId}/${id}`} 
                            onClick={e => e.stopPropagation()} style={{ color: textColor}}>
                        More
                    </Link> 
                </Button>
            }
               
                
            </div>
            
        )
    }
}

export default withStyles(styles)(ColorBox);
