import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import PaletteNavbar from './PaletteNavbar'
import ColorBox from './ColorBox'
import Grid from '@material-ui/core/Grid'
import chroma from 'chroma-js'
import './PaletteShades.css'

class PaletteShades extends Component {
 
    getShades(){
        let shadeObj =[]
        const baseColor = this.props.color.color;
        const name = this.props.color.name;
        let makeShades = [0.9, 0.8, 0.7, 0.6, 0.5, 0.4, 0.3, 0.2, 0.1] 
        let colorShade = chroma.scale(baseColor).scale(10);
        for(let i = 0; i < makeShades.length; i++){
            // let colorShade = chroma(baseColor).luminance(makeShades[i]).hex()
            let colorShade = chroma.scale(baseColor).scale(10);
            let data = {
                name: name + " " +(1000 - makeShades[i]*1000),
                color: colorShade
            }
            shadeObj.push(data)
        }

        return shadeObj;
    }

    //   getShades(){
    //     let shadeObj =[]
    //     const baseColor = this.props.color.color;
    //     const name = this.props.color.name;
    //     // let makeShades = [0.9, 0.8, 0.7, 0.6, 0.5, 0.4, 0.3, 0.2, 0.1] 
    //     let makeShades = chroma.scale(baseColor).scale(10);
    //     for(let i = 0; i < makeShades.length; i++){
    //         // let colorShade = chroma(baseColor).luminance(makeShades[i]).hex()
    //         let colorShade = chroma.scale(baseColor).scale(10);
    //         let data = {
    //             name: name + " " +(1000 - makeShades[i]*1000),
    //             color: colorShade
    //         }
    //         shadeObj.push(data)
    //     }

    //     return shadeObj;
    // }

    render(){
        let shades = this.getShades();
        const baseColor = this.props.color.color;
        // const name = this.props.color.name;
        let ScaleShades = chroma.scale(baseColor).scale(10);
        console.log(ScaleShades)
          
        return(
            <div className="PaletteShades">
                <PaletteNavbar handleFormat = {this.props.handleFormat} format = {this.props.format}/>
               
                <Link to = {`/palette/${this.props.palette.id}`}>Go back</Link>
                   <Grid container spacing={0}>
                    {
                        shades.map(bgColor => (
                            <ColorBox 
                                colorName = {bgColor.name} 
                                bgColor = {
                                    (this.props.format === 'hex' &&  chroma(bgColor.color).hex()) ||
                                    (this.props.format === "rgb" && chroma(bgColor.color).css()) ||
                                    (this.props.format === "rgba" && chroma(bgColor.color).alpha(0.9).css())                                                             
                                }
                                onCopy = {this.props.onCopy}
                                format = {this.props.format}
                                                                                    
                            />
                         
                        ))
                    }
                </Grid>        
                
               

            </div>
        )
    }
}

export default PaletteShades;
