import React, { Component } from 'react';
import { Link } from 'react-router-dom'
import {CopyToClipboard} from 'react-copy-to-clipboard'
// import {SortableContainer, SortableElement} from 'react-sortable-hoc';
// import ColorBox from './ColorBox'
import PaletteFooter from './PaletteFooter'
import PaletteNavbar from './PaletteNavbar'
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import chroma from 'chroma-js'

class ColorPalette extends Component {
    constructor(props){
        super(props);
        this.handleCopy = this.handleCopy.bind(this)
    }

    handleCopy(evt){
       this.props.onCopy(true)
    }

    render(){

        const palette = this.props.palette;
                      
        return(
            <div className="ColorPalette">
         
               <PaletteNavbar handleFormat = {this.props.handleFormat} format = {this.props.format} />
               <div>
                    <Link to = "/">Go back</Link>
                </div>
               <Grid container spacing={0}>
                    {
                        palette.colors.map(color => (
                            <Grid item xs={12} sm={3} style={{height: "25vh",position: "relative", 
                                backgroundColor: color.color,
                                    color: chroma.contrast(color.color, 'white') >= 4.5 ? "white" : "black"
                                }}>
                            
                                 <div style={{position: "absolute", left: "0", bottom: "0", marginLeft: "10px", textTransform: "uppercase"}}>{color.name}</div>
                               
                                 <p>{
                                        (this.props.format === 'hex' &&  chroma(color.color).hex()) ||
                                        (this.props.format === "rgb" && chroma(color.color).css()) ||
                                        (this.props.format === "rgba" && chroma(color.color).alpha(0.9).css())  
                                    }
                                </p>

                                <CopyToClipboard onCopy = {this.handleCopy} 
                                                text={(this.props.format === 'hex' &&  chroma(color.color).hex()) ||
                                                        (this.props.format === "rgb" && chroma(color.color).css()) ||
                                                        (this.props.format === "rgba" && chroma(color.color).alpha(0.9).css())}
                                >
                                    <Button style={{ color: chroma.contrast(color.color, 'white') >= 4.5 ? "white" : "black"}}>Copy</Button>
                                </CopyToClipboard>
                                                    
                                <Link to = {`/palette/${palette.id}/${color.name}`} 
                                        style={{ color: chroma.contrast(color.color, 'white') >= 4.5 ? "white" : "black", 
                                        position: "absolute", right: "0", bottom: "0"
                                        }}
                                >
                                    More
                                </Link>
                            </Grid> 
                        ))
                    }
                </Grid>

                <PaletteFooter paletteName = {palette.paletteName} />
            </div>
        )
    }
}

export default ColorPalette;
