import React, { Component } from 'react';
import {Link} from 'react-router-dom'
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import {ChromePicker} from 'react-color'


class NewPaletteForm extends Component {
    constructor(props){
        super(props);
        this.state = {
            bgColor: "red",
          
        }
        this.handleChange = this.handleChange.bind(this)

    }
    handleChange(color) {
        this.setState({  bgColor: color.hex });
      };

   
    render(){

 
        return(
            <Grid container justify="center" className = "NewPaletteForm">
                <Grid container style={{width: "30%", flexDirection: "column"}}>
                    <Button variant="contained" color="primary">
                        Random Color
                    </Button>
                    <Button variant="contained" color="secondary">
                        Clear Palette
                    </Button>
                    <ChromePicker style={{height: "1000px"}} color={this.state.bgColor}
                                onChange = {this.handleChange}
                    />
                </Grid>
            
                <Grid item style={{width: "70%"}}>
                <div>
                    <button>Save</button>
                    <Link to = "/"> Go Back</Link>
                </div>
                   
                    
                    <div style={{backgroundColor: this.state.bgColor, height: "200px"}}>
                        To change background color
                    </div>
                </Grid>
            </Grid>
         
        )
    }
}

export default NewPaletteForm;
