import React, { Component } from 'react';
import { Route, Switch, Redirect } from 'react-router-dom'
import seedColors from './seedColors'
import ColorPalette from './ColorPalette'
import ColorPaletteList from './ColorPaletteList'
import PaletteShades from './PaletteShades'
import NewPaletteForm from './NewPaletteForm';
import {getPaletteShades} from './helpers'

class Routes extends Component {
    static defaultProps = {
        colorPalettes: seedColors,
        marks: {50: "50", 100: "100", 200: "200", 300: "300", 400: "400", 500: "500", 600: "600", 700: "700", 800: "800", 900: "900"}
    }
      constructor(props){
        super(props);
        this.state = {
            format: "hex",
            copied: false
        }
    
        this.handleFormat = this.handleFormat.bind(this)
        this.onCopy = this.onCopy.bind(this)
      }
    
      handleFormat(value){
          this.setState({
              format: value,
          })
      }
      onCopy(value){
        this.setState({
          copied: value
        })
      }
       
    render(){
        const getPalette = props => {
            let name = props.match.params.name;
                 
            let currentPalette = this.props.colorPalettes.find(
              palette => palette.id === name
              
            );
            return <ColorPalette {...props} palette={getPaletteShades(currentPalette)} 
                    format={this.state.format} 
                    handleFormat = {this.handleFormat}
                    onCopy = {this.onCopy} 
                    sliderMarks = {this.props.marks}
                    />;
        };
      
        const getColorShades = props => {
            let name = props.match.params.name;
            let colorName = props.match.params.colorName.replace(/\s/g, "-");
            console.log(colorName, name)
            // let currentPaletteShades = getColorShades(this.props.colorPalettes)
            let currentPalette = this.props.colorPalettes.find( 
              palette => palette.id === name
            )
            let currentColor = currentPalette.colors.find(
              color => color.name.replace(/\s/g, "-") === colorName.replace(/\s/g, "-")
            )
            return <PaletteShades {...props} color={currentColor} 
                    palette = {currentPalette} 
                    format={this.state.format} 
                    handleFormat = {this.handleFormat}
                    onCopy = {this.onCopy} 
                  />
        }

        return(
            <Switch>
                <Route exact path="/" render = {() => <ColorPaletteList colorPalettes={this.props.colorPalettes} />} />
                <Route exact path = "/palette/:name" render = {getPalette} />
                <Route exact path = {`/palette/:name/:${colorName.replace(/\s/g, "-")}`} render ={getColorShades} />
                <Route exact path = "/palette" component = {NewPaletteForm} />
                <Redirect to = "/" />
            </Switch>

        )
    }
}

export default Routes;
