import React, { Component } from 'react';
import { Link } from 'react-router-dom'
import ColorBox from './ColorBox'
import PaletteFooter from './PaletteFooter'
import PaletteNavbar from './PaletteNavbar'
import Grid from '@material-ui/core/Grid';
import chroma from 'chroma-js'

class ColorPalette extends Component {
    constructor(props){
        super(props);
        this.state = {
            format: "hex"
        }
    
        this.handleFormat = this.handleFormat.bind(this)
    }

    handleFormat(value){
        this.setState({
            format: value,
        })
    }

    render(){

        const palette = this.props.palette;
        console.log(palette)

           
        return(
            <div className="ColorPalette">
         
               <PaletteNavbar handleFormat = {this.handleFormat} format = {this.state.format} />
               <Grid container spacing={0}>
                     <Link to = "/palette">Go back</Link>
                    {
                        palette.colors.map(color => (
                            <Grid item xs={12} sm={3} >
                                <ColorBox  
                                    colorName = {color.name}
                                    key = {color.name}
                                    colorCode = {
                                        (this.state.format === 'hex' &&  chroma(color.color).hex()) ||
                                        (this.state.format === "rgb" && chroma(color.color).css()) ||
                                        (this.state.format === "rgba" && chroma(color.color).alpha(0.9))                                                             
                                    }
                                    id={palette.id}
                                    
                                />
                            </Grid>
                        ))
                    }
                </Grid>

                <PaletteFooter paletteName = {palette.paletteName} />
            </div>
        )
    }
}

export default ColorPalette;
