import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';
import { Card } from '@material-ui/core';
import 'emoji-mart/css/emoji-mart.css'
import { Picker } from 'emoji-mart'
import MainNavbar from './MainNavbar';
import { mergeClasses } from '@material-ui/styles';


class ColorPaletteList extends Component {


    render() {
        const useStyles = makeStyles(theme => ({
            root: {
              flexGrow: 1,
            },
            paper: {
              height: 140,
              width: 100,
            },
            control: {
              padding: theme.spacing(2),
            },
          }));
        const classes = useStyles();
        return(
            <Grid container className={classes.root} spacing={2}>
                 <MainNavbar />
            
                <Grid container spacing={2} className="ColorPaletteList">
                    <Grid item xs={12} >
                    <Grid container justify="center" spacing={3}>
            
                    {this.props.colorPalettes.map(palette => (
                        <Grid item  xs={12} md={4} style={{border: "1px solid gray"}}>
                        
                            {
                                palette.colors.map(color =>(
                                    <div style={{backgroundColor: color.color, height:"15%", width: "20%", display: "inline-block", margin: "0"}}>
                                        {/* {color.name} */}
                                    </div>
                                ))
                            } 
                            <Link to = {`/palette/${palette.id}`}>{palette.paletteName}</Link> 
                            <div>{palette.emoji}</div>
                        </Grid>              
                    
                    ))}
                    </Grid>
                    </Grid>
                    {/* <Picker set='emojione' /> */}
                    {/* <Picker onSelect={this.addEmoji} />
                    <Picker title='Pick your emoji…' emoji='point_up' />
                    <Picker style={{ position: 'absolute', bottom: '20px', right: '20px' }} /> */}
                    {/* <Picker i18n={{ search: 'Recherche', categories: { search: 'Résultats de recherche', recent: 'Récents' } }} /> */}
                </Grid>
            </Grid>
        )
    }
}

export default ColorPaletteList;
