import React, { Component } from 'react';
import {Link} from 'react-router-dom'
import {ChromePicker} from 'react-color'
import ColorBox from './ColorBox'
import seedColors from './seedColors'
// import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import chroma from 'chroma-js'
// import DeleteIcon from '@icons/material/DeleteIcon';
// import { Card, Container, IconButton } from '@material-ui/core';
import './NewPaletteForm.css'



class NewPaletteForm extends Component {
    static defaultProps = {
        maxColors: 20,
    }
    constructor(props){
        super(props);
        this.state = {
    
            paletteName: "",
            id: "",
            emoji: "",
            colors: [
                { name: "red", color: "#FF0000"},
                { name: "yellow", color: "#FFB000"},
                { name: "red", color: "#FF0000"},
                { name: "yellow", color: "#FFB000"},
                { name: "red", color: "#FF0000"},
                { name: "yellow", color: "#FFB000"},
                { name: "red", color: "#FF0000"},
                { name: "yellow", color: "#FFB000"},
                { name: "red", color: "#FF0000"},
                { name: "yellow", color: "#FFB000"},
                { name: "red", color: "#FF0000"},
                { name: "yellow", color: "#FFB000"},
            ],
            name: "",
            color: "",
            bgColor: "rgb(220, 0, 78)",
            open: false,
            disabled: false,          
        }
        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleClick = this.handleClick.bind(this)
        this.handleClickOpen = this.handleClickOpen.bind(this)
        this.handleClose = this.handleClose.bind(this)
        this.handleSave = this.handleSave.bind(this)
        this.paletteName = this.paletteName.bind(this)
        this.addColorName = this.addColorName.bind(this)
        this.addPaletteToPaletteList = this.addPaletteToPaletteList.bind(this)
        this.chooseRandomColor = this.chooseRandomColor.bind(this);
        this.clearPalette = this.clearPalette.bind(this);

    }

    // adding the palette to paletteList
    addPaletteToPaletteList(){
        const {paletteName, emoji, colors} = this.state;
        let id = paletteName.toLocaleLowerCase().split(' ').join("-");
        let palettes = seedColors;
        let newPalette = {
            paletteName: paletteName,
            id: id,
            emoji: emoji,
            colors: colors
        }
        palettes.push(newPalette)
    }


    handleChange(color) {
        let newColor = color.hex
   
        this.setState({
            color:newColor,
            bgColor:newColor,
        })
    };

  
    addColorName(evt){
       
        this.setState({
            [evt.target.name]: evt.target.value, 
        })
        
    }

    handleSubmit(evt){
        evt.preventDefault();

    }

    handleClick(evt){
       
        let size = this.state.colors.length+1;
        if (size <= this.props.maxColors){
            let newColorName = this.state.name;
            let newColorCode = this.state.color;
                
            let toAdd = {
                name: newColorName,
                color: newColorCode,
            }
            this.setState(st => ({
                colors: [...st.colors,  toAdd],
                size: size,
                name: "",
                color: "",
                bgColor: "rgb(220, 0, 78)",
                disabled: false,
            }))
        }
        else if(size === this.props.maxColors){
            this.setState(st =>({
                // colors: [...st.colors],
                // name: "",
                // color:"",
                bgColor: "rgba(0, 0, 0, 0.12)",
                disabled: true,
            }))
        }
       
      
         
    }
   
// opens modal form for palette name
    handleClickOpen() {
        this.setState({
            open: true,
        })
    
    }

// close modal form
    handleClose(evt) {
        this.setState({
            open:false,
        })
    }

// when save is clicked, palette is added to the palette list
    handleSave(){
        this.addPaletteToPaletteList();
        this.setState({
            open:false,
        })
    }
// set the palette name with modal form
    paletteName(evt){
        this.setState({
            paletteName: evt.target.value, 
        })
    }
// random color 
    chooseRandomColor(){
        let newColor = chroma.random();
        this.setState({
            color: newColor,
            bgColor: newColor,
        })
      
    }

// clear the palette
    clearPalette(){
        this.setState({
            colors: []
        })
    }
 
    render(){
        const {bgColor, color, name, open, paletteName, colors} =  this.state;
        // set the text color, based on the contrast with the background
        const textColor = chroma.contrast(bgColor, 'white') >= 4.5 ? "white" : "black"
        return(
            <div className = "NewPaletteForm" >
                <div className="color-picker">
                    <h2>Design Your Palette</h2>
                    <Button variant="contained" color="primary" onClick={this.chooseRandomColor} disabled={this.state.disabled}>
                        Random Color
                    </Button>
                    <Button variant="contained" color="secondary" onClick={this.clearPalette}>
                        Clear Palette
                    </Button>
                    <ChromePicker color={color}
                         onChangeComplete = {this.handleChange}
                    />
                    <form onSubmit = {this.handleSubmit}>  
                        <TextField
                            required
                            id="filled-with-placeholder"
                            label="Color Name"
                            placeholder="Color Name"
                            margin="normal"
                            variant="filled"
                            value = {name}
                            name="name"
                            onChange={this.addColorName}
                            disabled={this.state.disabled}
                        />
                        <Button  variant="contained" onClick = {this.handleClick} 
                                style={{backgroundColor: this.state.disabled ? "rgba(0, 0, 0, 0.12)" : bgColor, color: textColor}} 
                                disabled={this.state.disabled}
                        >
                            Add Color
                        </Button>
                    </form>
                </div>

                <div className="color-container">
                    <div className="color-container-navbar">
                        <h5>Create A Palette</h5>
                        <div className="navbar-button">
                            <Button variant="contained" color="primary">
                                <Link to = "/"> Go Back</Link>
                            </Button>
                            
                            <Button variant="contained" color="secondary" onClick={this.handleClickOpen}>
                                Save
                            </Button>
                        </div>
                        
                    </div>
                    <Dialog open={open} onClose={this.handleClose} aria-labelledby="form-dialog-title">
                        <DialogTitle id="form-dialog-title"> Add Palette Name</DialogTitle>

                        <DialogContent>
                            <DialogContentText>
                                Please Add Palette Name
                            </DialogContentText>

                            <TextField
                                autoFocus
                                margin="dense"
                                id="name"
                                value={paletteName}
                                label="Palette Name"
                                type="name"
                                fullWidth
                                onChange = {this.paletteName}
                            />
                        </DialogContent>

                        <DialogActions>
                            <Button onClick={this.handleClose} color="primary">
                                Cancel
                            </Button>
                            <Button onClick={this.handleSave } color="primary">
                                Save
                            </Button>
                        </DialogActions>
                    </Dialog>

                    <div className="color-container-palette"> 
                        {colors.map(color => (
                            
                                <ColorBox
                                //  bgColor = {color.color}
                                bgColor = {
                                        (this.props.format === 'hex' &&  chroma(color.color).hex()) ||
                                        (this.props.format === "rgb" && chroma(color.color).css()) ||
                                        (this.props.format === "rgba" && chroma(color.color).alpha(0.9).css())                                                             
                                    }
                                         colorName = {color.name}
                                         key = {color.color}
                                         format = {this.props.format}
                                />
                              
                        ))}
                    </div>
                </div>
               
            </div>
         
        )
    }
}

export default NewPaletteForm;
