import React, { Component } from 'react'
// import {Link} from 'react-router-dom'
import {CopyToClipboard} from 'react-copy-to-clipboard'
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import chroma from 'chroma-js'

class ColorBox extends Component {
    constructor(props){
        super(props);
        this.handleCopy = this.handleCopy.bind(this)
    }

    handleCopy(evt){
       this.props.onCopy(true)
    }
    render(){
        let bgColor = this.props.colorCode; 
        let textColor = chroma.contrast(bgColor, 'white') >= 4.5 ? "white" : "black"
        return(
            <Grid item xs={12} sm={3} className="ColorBox"  
                style={{backgroundColor: bgColor, color: textColor, height: "25vh"}}
            >
                    <div style={{alignSelf:"flex-end"}}>
                        {this.props.colorName} 
                    </div>
                   
                    <div>
                        {this.props.colorCode}
                    </div>
        
                    {/* <div style={{alignSelf: "center"}}>
                        Copy
                    </div> */}
                    
                    <CopyToClipboard onCopy = {this.handleCopy} 
                            text={(this.props.format === 'hex' &&  chroma(this.props.colorCode).hex()) ||
                            (this.props.format === "rgb" && chroma(this.props.colorCode).css()) ||
                            (this.props.format === "rgba" && chroma(this.props.colorCode).alpha(0.9).css()) }>
                         <Button 
                                style={{ color: chroma.contrast(color.color, 'white') >= 4.5 ? "white" : "black", 
                                        position: "absolute", left: "50%", top: "50%", transform: "translate(-50%, -50%)"
                                    
                                    }}
                        >
                            Copy
                        </Button>
                    </CopyToClipboard>
            </Grid>
        )
    }
}

export default ColorBox;
