import mediaquery from './mediaquery'
export default {
    root: {
        alignItems: "center",
        display: "flex",
        fontWeight: 'bold',
        justifyContent: "flex-end",
        
        height: "4vh",
        [mediaquery.down('sm')]: {
            display: props => !props.showingFullPalette ? 'flex' : 'none',
            height:  props => !props.showingFullPalette ? "4vh" : "0"
        },
        [mediaquery.down('xs')]: {
            display: props => !props.showingFullPalette ? 'flex' : 'none',
            height:  props => !props.showingFullPalette ? "4vh" : "0"
        },
        

    },
    emoji: {
        fontSize: "1.3rem",
        margin: "0 1rem",
    }
}
