import React, { Component } from 'react'
import ColorPalette from './ColorPalette'
import {Link} from 'react-router-dom'
import seedColors from './seedColors'
import Grid from '@material-ui/core/Grid';


class ColorPaletteList extends Component {
    static defaultProps = {
        colorPalettes: seedColors
    }
    render() {
        return(
            <Grid container spacing={5} className="ColorPaletteList">
                {this.props.colorPalettes.map(palette => (
                <Grid item xs={12} sm={3} style={{border: "1px solid gray"}}>
                    <li>{palette.paletteName}
                    {
                        palette.colors.map(color =>(
                            <div style={{backgroundColor: color.color, height: "20px", width: "20%"}}>
                                {/* {color.name} */}
                            </div>

                        ))
                    }
{/*                  
                        <ColorPalette colors={palette.colors}/> */}
                    </li>
                </Grid> 
                ))}
                

            </Grid>
        )
    }
}

export default ColorPaletteList;
