import React, {Component} from 'react'
import Slider from 'rc-slider';
import {Link} from 'react-router-dom'
import Snackbar from '@material-ui/core/Snackbar';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@icons/material/CloseIcon';
import 'rc-slider/assets/index.css'
// import Button from '@material-ui/core/Button';
// import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
// import { Link } from 'react-router-dom'

import Select from '@material-ui/core/Select';
import './PaletteNavbar.css'

class PaletteNavbar extends Component {

    constructor(props){
        super(props);
        this.state={
            value: 500,
            open: false,
        }
          
        this.handleFormatChange = this.handleChange.bind(this)
        this.handleShadeChange = this.handleShadeChange.bind(this)
        this.closeSnackbar = this.closeSnackbar.bind(this)
    }

    handleFormatChange(evt){
        this.props.handleFormat(evt.target.value)
        this.setState({
            open: true,
        })
    }

    handleShadeChange(value){
        this.props.changeShade(value);
        this.setState({
            value: value,
        })
        
    }
    closeSnackbar(){
        this.setState({open: false})
    }

    render(){
            
        return(
            <div className="PaletteNavbar">
                <div className="LogoName">
                    <h3>
                        <Link to="/">ColorPalettes</Link>
                    </h3>
                </div>
                <div className="PaletteNavbar-slider">
                    <span className="Slider-legend">
                        Level: [{this.state.value}]
                    </span>
                    <div className="Slider-container">
                        <Slider 
                            defaultValue={this.state.value}
                            step={100}
                            min={100}
                            max={900}
                            onChange={this.handleShadeChange}
                        />
                    </div>
                </div>
                <div className="PaletteNavbar-select">
                    <Select value = {this.props.format} onChange={this.handleFormatChange}>
                        <MenuItem value = 'hex'>HEX - #FFFFFF</MenuItem>
                        <MenuItem value='rgb'>RGB - rgb(255, 255, 255)</MenuItem>
                        <MenuItem value='rgba'>RGBA - rgba(255, 255, 255, 1.0)</MenuItem>
                    </Select>
                </div>
                {/* <div className="ColorPalette-link">
                        <Link to = "/">Go back</Link>
                    </div> */}
                    <Snackbar
                    anchorOrigin={{
                        vertical: 'bottom',
                        horizontal: 'left',
                    }}
                    open={this.state.open}
                    autoHideDuration={3000}
                    message={<span id="message-id">Format Changed!</span>}
                    ContentProps={{
                        'aria-describedby': 'message-id',
                    }}
                    onClose={this.closeSnackbar}
                    
                    action={[
                    <IconButton
                        key="close"
                        aria-label="close"
                        color="inherit"
                        // className={classes.close}
                        onClick={this.closeSnackbar}
                    >
                        <CloseIcon />
                    </IconButton>,
                    ]}
                />
            </div>
        )
    }
}

export default PaletteNavbar;
