import React, { Component } from 'react';
import {Link} from 'react-router-dom'
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import ButtonGroup from '@material-ui/core/ButtonGroup';
import {ChromePicker} from 'react-color'


class NewPaletteForm extends Component {
    constructor(props){
        super(props);
        this.state = {
            bgColor: "red",
          
        }
        this.handleChange = this.handleChange.bind(this)

    }
    handleChange(color) {
        this.setState({  bgColor: color.hex });
      };

   
    render(){

 
        return(
            <Grid container justify="center" className = "NewPaletteForm" style={{height: "100vh"}}>
                <Grid container justify="center" style={{backgroundColor:"gray", width: "20%", flexDirection: "column", }}>
                    <ButtonGroup>
                        <Button variant="contained" color="primary">
                            Random Color
                        </Button>
                        <Button variant="contained" color="secondary">
                            Clear Palette
                        </Button>
                    </ButtonGroup>
                    <ChromePicker color={this.state.bgColor}
                                onChange = {this.handleChange}
                    />
                </Grid>
            
                <Grid container style={{width: "80%"}}>
                <div>
                    <button>Save</button>
                    <Link to = "/"> Go Back</Link>
                </div>
                   
                    
                    <div style={{backgroundColor: this.state.bgColor, height: "200px"}}>
                        To change background color
                    </div>
                </Grid>
            </Grid>
         
        )
    }
}

export default NewPaletteForm;
