import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import Grid from '@material-ui/core/Grid';
// import Paper from '@material-ui/core/Paper';
// import { makeStyles } from '@material-ui/core/styles';
import { Card, Container, IconButton } from '@material-ui/core';
import Button from '@material-ui/core/Button';
import SaveIcon from '@material-ui/icons/Save';

import 'emoji-mart/css/emoji-mart.css'
// import { Picker } from 'emoji-mart'
import MainNavbar from './MainNavbar';
// import { mergeClasses } from '@material-ui/styles';


class ColorPaletteList extends Component {


    render() {
    
        return(
            <Container>
       
            <Grid container justify="center" className="ColorPaletteList">
                <MainNavbar />
            <SaveIcon variant="contained" color="secondary" >
                Delete
                
            </SaveIcon>
                <Grid item xs={12}>
                    <Grid container justify="center" spacing={10}>
                               
                        {this.props.colorPalettes.map(palette => (
                             <Grid item xs={12} sm={4}>
                             
                                <Card key={palette.id} item style={{border: "1px solid gray", padding: "10px"}}>
                                <Grid container justify="center">
                                
                                    {
                                        palette.colors.map(color =>(
                                            <Grid  item key ={color.color} style={{backgroundColor: color.color, height:"60px", width: "60px", display: "inline-block", margin: "0"}}>
                                            </Grid>
                                        ))
                                    } 
                                    </Grid>
                                    <div style={{marginTop: "20px", width: "100%"}}>
                                        <div style={{display: "inline-block", width: "80%", textAlign: "left", paddingLeft: "15px"}}><Link to = {`/palette/${palette.id}`}>{palette.paletteName}</Link> </div>
                                        
                                        <div style={{display: "inline-block", width: "20%", textAlign: "right", paddingRight: "15px"}}>{palette.emoji}</div>
                                    </div>
                                </Card> 
                            </Grid>             
                        
                        ))}
                    </Grid>
                </Grid>
                {/* <Picker set='emojione' /> */}
                {/* <Picker onSelect={this.addEmoji} />
                <Picker title='Pick your emoji…' emoji='point_up' />
                <Picker style={{ position: 'absolute', bottom: '20px', right: '20px' }} /> */}
                {/* <Picker i18n={{ search: 'Recherche', categories: { search: 'Résultats de recherche', recent: 'Récents' } }} /> */}
            </Grid>
            </Container>
        )
    }
}

export default ColorPaletteList;
