import React, { Component } from 'react'
import chroma from 'chroma-js'
// import { scaleThreshold } from 'd3-scale';

class PaletteShades extends Component {
    // palette(min, max) {
    //     const d = (max-min)/9;
    //     const originalColor = this.props.color.color;
    //     const lighter = chroma(originalColor).brighten(1);
    //     const lightest = chroma(originalColor).luminance(0.9)
    //     const darkest = chroma(originalColor).darken(4)
    //     return scaleThreshold()
    //         .range([lightest, lighter, originalColor, darkest])
    //         .domain([min + d*1,min + d*2,min + d*3,min + d*4,min + d*5,min + d*6,min + d*7,min + d*8]);
    // }
    render(){
        const originalColor = this.props.color.color;
        const lighter = chroma(originalColor).brighten(1);
        const lightest = chroma(originalColor).luminance(0.9)
        const darkest = chroma(originalColor).darken(4)
        const shades = chroma.scale([lightest, lighter, originalColor, darkest])
        console.log(lightest, lighter, originalColor, darkest, shades)
        return(
            <div>
                <h1>This is from shades</h1>
                <div style={{backgroundColor:  this.props.color.color }}>
                    This is original color
                </div>
                <div style={{backgroundColor:  chroma(this.props.color.color).brighten(0.1) }}>
                    This is brighter 0.1
                </div>
                <div style={{backgroundColor:  chroma(this.props.color.color).darken(0.8) }}>
                    This is darker color 0.8
                </div>
                <div style={{backgroundColor:  chroma(this.props.color.color).darken(1) }}>
                    This is darker color 1
                </div>
                <div style={{backgroundColor:  chroma(this.props.color.color).darken(2) }}>
                    This is darker color 2
                </div>
                <div style={{backgroundColor:  chroma(this.props.color.color).darken(3) }}>
                    This is darker color 3
                </div>
                <div style={{backgroundColor:  chroma(this.props.color.color).darken(4) }}>
                    This is darker color 4
                </div>
                <div style={{backgroundColor:  chroma(this.props.color.color).brighten(0.2) }}>
                    This is brighter 0.2
                </div>
                <div style={{backgroundColor:  chroma(this.props.color.color).brighten(0.3) }}>
                    This is brighter 0.3
                </div>
                <div style={{backgroundColor:  chroma(this.props.color.color).brighten(0.4) }}>
                    This is brighter 0.4
                </div>
                <div style={{backgroundColor:  chroma(this.props.color.color).brighten(0.5) }}>
                    This is brighter 0.5
                </div>
                <div style={{backgroundColor:  chroma(this.props.color.color).brighten(1) }}>
                    This is brighter 1
                </div>
                <div style={{backgroundColor:  chroma(this.props.color.color).brighten(1.5) }}>
                    This is brighter 1.5
                </div>
                <div style={{backgroundColor:  chroma(this.props.color.color).luminance(0.1) }}>
                    This is luminance 0.1
                </div>
                <div style={{backgroundColor:  chroma(this.props.color.color).luminance(0.2) }}>
                    This is luminance 0.2
                </div>
                <div style={{backgroundColor:  chroma(this.props.color.color).luminance(0.3) }}>
                    This is luminance 0.3
                </div>
                <div style={{backgroundColor:  chroma(this.props.color.color).luminance(0.4) }}>
                    This is luminance 0.4
                </div>
                <div style={{backgroundColor:  chroma(this.props.color.color).luminance(0.5) }}>
                    This is luminance 0.5
                </div>
                <div style={{backgroundColor:  chroma(this.props.color.color).luminance(0.6) }}>
                    This is luminance 0.6
                </div>
                <div style={{backgroundColor:  chroma(this.props.color.color).luminance(0.7) }}>
                    This is luminance 0.7
                </div>
                <div style={{backgroundColor:  chroma(this.props.color.color).luminance(0.8) }}>
                    This is luminance 0.8
                </div>
                <div style={{backgroundColor:  chroma(this.props.color.color).luminance(0.9) }}>
                    This is luminance 0.9
                </div>
               

            </div>
        )
    }
}

export default PaletteShades;
