import React, { Component } from 'react'
import {ChromePicker} from 'react-color'
import {Link} from 'react-router-dom'
import { ValidatorForm, TextValidator} from 'react-material-ui-form-validator';
import classNames from 'classnames'
import { withStyles } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import CssBaseline from '@material-ui/core/CssBaseline';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@icons/material/MenuIcon';
import ChevronLeftIcon from '@icons/material/ChevronLeftIcon';
import Button from '@material-ui/core/Button';
import DraggableColorBox from './DraggableColorBox'


const drawerWidth = 400;

const styles = theme => ({
  root: {
    display: 'flex',
  },
  appBar: {
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShift: {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: drawerWidth,
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  hide: {
    display: 'none',
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
  },
  drawerHeader: {
    display: 'flex',
    alignItems: 'center',
    padding: theme.spacing(0, 1),
    ...theme.mixins.toolbar,
    justifyContent: 'flex-end',
  },
  content: {
    flexGrow: 1,
    height: "calc(100vh - 64px)",
    padding: theme.spacing(3),
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: -drawerWidth,
  },
  contentShift: {
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: 0,
  },
});

class MakeNewPalette extends Component {
    constructor(props){
        super(props);
        this.state = {
            open: false,
            currentColor: "teal",
            newColorName: "",
            colors: [
              { name: "red", color: "#ff0000"},
              { name: "yellow", color: "#ffb000"},
    
            ],
            paletteName: "New Palette",
            id:"new-palette",
            emoji:":)",
            openModal: false,
            openEmoji: false,
        }

        this.handleColorPickerChange = this.handleColorPickerChange.bind(this)
        this.addNewColor = this.addNewColor.bind(this)
        this.handleNewColorName = this.handleNewColorName.bind(this)
        this.savePalette = this.savePalette.bind(this)
    }

    componentDidMount() {
      // validate if the color name is unique
      ValidatorForm.addValidationRule('isColorNameUnique', value => 
        this.state.colors.every(({ name }) => name.toLowerCase() !== value.toLowerCase())
      );

    //  validate if the color is unique
      ValidatorForm.addValidationRule('isColorUnique', value => 
        this.state.colors.every(({ color }) => color !== this.state.currentColor)
      );
    }
  
    handleDrawerOpen = () => {
        this.setState({open: true});
      };

    handleDrawerClose = () => {
      this.setState({open: false});
    };

    handleColorPickerChange(newColor){
        this.setState({
            currentColor: newColor.hex,
        })
    }

    addNewColor(){
    
      const newColorObj = {
        name: this.state.newColorName,
        color: this.state.currentColor,
      }
      this.setState({
        colors: [...this.state.colors, newColorObj],
        newColorName: "",
      });
    }

    handleNewColorName(evt){
      let newName = evt.target.value;
      this.setState({
        newColorName: newName,
      })
    }

    savePalette(){
      const newPalette = {
        paletteName: this.state.paletteName,
        id: this.state.id,
        emoji: this.state.emoji,
        colors: this.state.colors,
      }
      this.props.savePalette(newPalette)
    }

    render(){
        const {classes} = this.props;
        const {open, currentColor, colors, newColorName} = this.state;

        return(
            <div className={classes.root}>
                <CssBaseline />
                <AppBar
                  color="default"
                    position="fixed"
                    className={classNames(classes.appBar, {
                        [classes.appBarShift]: open,
                    })}
                >
                    <Toolbar>
                        <IconButton
                            color="inherit"
                            aria-label="open drawer"
                            onClick={this.handleDrawerOpen}
                            edge="start"
                            className={classNames(classes.menuButton, open && classes.hide)}
                        >
                            <MenuIcon />
                        </IconButton>
                        <Typography variant="h6" noWrap>
                            Create A Palette
                        </Typography>
                        <div>
                          <Button variant="contained" color="secondary">
                              <Link to = "/"> Go Back</Link>
                          </Button>
                          
                          <Button variant="contained" color="primary" onClick={this.savePalette}>
                              Save Palette
                          </Button>
                        </div>
                    </Toolbar>
                 
                </AppBar>
                <Drawer
                  className={classes.drawer}
                  variant="persistent"
                  anchor="left"
                  open={open}
                  classes={{
                      paper: classes.drawerPaper,
                  }}
                >
                    <div className={classes.drawerHeader}>
                        <IconButton onClick={this.handleDrawerClose}>
                             <ChevronLeftIcon /> 
                        </IconButton>
                    </div>
                  <Divider />
                  <Typography variant="h4" noWrap>
                      Design Your Palette
                  </Typography>
                  <div>
                      <Button variant="contained" color="primary" onClick={this.chooseRandomColor}>
                          Random Color
                      </Button>
                      <Button variant="contained" color="secondary" onClick={this.clearPalette}>
                          Clear Palette
                      </Button>
                  </div>
                  
                  <ChromePicker 
                    color={currentColor} 
                    onChangeComplete = {this.handleColorPickerChange}
                  />


                  <ValidatorForm 
                    onSubmit={this.addNewColor}
                  
                  >
                    <TextValidator 
                      value={newColorName}
                      onChange={this.handleNewColorName}
                      validators={[
                          'required', 
                          'isColorNameUnique', 
                          'isColorUnique'
                          ]}
                      errorMessages={[
                          'This field is required. Enter a color name!', 
                          'Color name must be unique!', 
                          'Color already exists!'
                          ]}
                    />

                  <Button 
                    type="submit"
                    variant="contained" 
                    style={{backgroundColor: currentColor}}
                  >
                      Add Color
                  </Button>
                  </ValidatorForm>
                </Drawer>

                <main className={classNames(classes.content, {
                        [classes.contentShift]: open,
                    })}
                >
                <div className={classes.drawerHeader} />
                                            
                  {colors.map(color => (
                  
                      <DraggableColorBox color={color.color} name={color.name} />
                  ))}
                    
            
                </main>
          </div>
        )
    }
}

export default withStyles(styles, {withTheme: true}) (MakeNewPalette);
