import React, { Component } from 'react'
// import {Link} from 'react-router-dom'
import {CopyToClipboard} from 'react-copy-to-clipboard'
import Grid from '@material-ui/core/Grid';
import chroma from 'chroma-js'

class ColorBox extends Component {
    render(){
        let bgColor = this.props.colorCode; 
        let textColor = chroma.contrast(bgColor, 'white') >= 4.5 ? "white" : "black"
        return(
            <Grid item xs={12} sm={3} className="ColorBox"  
                style={{backgroundColor: bgColor, color: textColor, height: "25vh"}}
            >
                    <div style={{alignSelf:"flex-end"}}>
                        {this.props.colorName} 
                    </div>
                   
                    <div>
                        {this.props.colorCode}
                    </div>
        
                    <div style={{alignSelf: "center"}}>
                        Copy
                    </div>
                    
                    <CopyToClipboard onCopy = {this.handleCopy} text={(this.props.format === 'hex' &&  chroma(color.color).hex()) ||
                                        (this.props.format === "rgb" && chroma(color.color).css()) ||
                                        (this.props.format === "rgba" && chroma(color.color).alpha(0.9).css()) }>
                                    <button>Copy</button>
                                </CopyToClipboard>
            </Grid>
        )
    }
}

export default ColorBox;
