import React, { Component } from 'react'
import {Link} from 'react-router-dom'
import {CopyToClipboard} from 'react-copy-to-clipboard'
// import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import chroma from 'chroma-js'
import './ColorBox.css'

class ColorBox extends Component {
    constructor(props){
        super(props);
        this.handleCopy = this.handleCopy.bind(this)
    }

    handleCopy(evt){
       this.props.onCopy(true)
    }
    render(){
        // let bgColor = this.props.colorCode; 
        const { bgColor, colorName, peletteId } = this.props; 
        let textColor = chroma.contrast(bgColor, 'white') >= 4.5 ? "white" : "black"
        return(
            <div className="ColorBox" style={{backgroundColor: bgColor, color: textColor}}>
                <div className="ColorBox-ColorName">
                    {colorName} 
                </div>

                {/* <span>MORE</span> */}
                
                {/* <div>
                    {this.props.colorCode}
                </div> */}
    
                {/* <div style={{alignSelf: "center"}}>
                    Copy
                </div> */}
                
                <CopyToClipboard 
                        onCopy = {this.handleCopy} 
                        text={(this.props.format === 'hex' &&  chroma(bgColor).hex()) ||
                            (this.props.format === "rgb" && chroma(bgColor).css()) ||
                            (this.props.format === "rgba" && chroma(bgColor).alpha(0.9).css()) }
                >

                    <Button style={{ color: chroma.contrast(bgColor, 'white') >= 4.5 ? "white" : "black", 
                        position: "absolute", left: "50%", top: "50%", transform: "translate(-50%, -50%)"
                    }}>
                        Copy
                    </Button>
                </CopyToClipboard>
                 <Link to = {`/palette/${peletteId}/${colorName}`} 
                        style={{ color: chroma.contrast(color.color, 'white') >= 4.5 ? "white" : "black", 
                        position: "absolute", right: "0", bottom: "0", marginRight: "10px", textTransform: "uppercase"
                        }}
                >
                    More
                </Link>
            </div>
        )
    }
}

export default ColorBox;
