import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import PaletteNavbar from './PaletteNavbar'
import ColorBox from './ColorBox'
import Grid from '@material-ui/core/Grid'
import chroma from 'chroma-js'

class PaletteShades extends Component {
 
    getShades(){
        var shades = [];
        let baseColor = this.props.color.color;
        // let darker = [1,1.5,2,2.5,3,4]
        let makeShades = [0.9, 0.8, 0.7, 0.6, 0.5, 0.4, 0.3, 0.2, 0.1] 

        for(let i = 0; i < makeShades.length; i++){
            let colorShade = chroma(baseColor).luminance(makeShades[i]).hex()
            shades.push(colorShade)
        }

        return shades;
    }

    render(){
        let shades = this.getShades();
        let name = this.props.color.name;
        let makeShades = [0.9, 0.8, 0.7, 0.6, 0.5, 0.4, 0.3, 0.2, 0.1] 
        let shadeObj =[]

        for( let i=0; i < shades.length; i++){
            let data = {
                name: name + (1000 - makeShades[i]*1000),
                color: shades[i]
            }
            shadeObj.push(data)
            
        }
        console.log(shadeObj)
        let nameArr =[];
        let value;
        let nameCode = makeShades.map(lum => (
           value= name + (1000 - lum*1000)
         
        ),   nameArr.push(value));
          
        return(
            <div>
                <PaletteNavbar handleFormat = {this.props.handleFormat} format = {this.props.format}/>
               
                <Link to = {`/palette/${this.props.palette.id}`}>Go back</Link>
                   <Grid container spacing={0}>
                    {
                        shades.map(bgColor => (
                            <ColorBox 
                                colorName = {name + (1000 - bgColor*1000)} 
                                colorCode = {
                                    (this.props.format === 'hex' &&  chroma(bgColor).hex()) ||
                                    (this.props.format === "rgb" && chroma(bgColor).css()) ||
                                    (this.props.format === "rgba" && chroma(bgColor).alpha(0.9).css())                                                             
                                }
                                onCopy = {this.props.onCopy}
                                format = {this.props.format}
                                                                                    
                            />
                         
                        ))
                    }
                </Grid>        
                
               

            </div>
        )
    }
}

export default PaletteShades;
