import React, { Component } from 'react';
import {Link} from 'react-router-dom'
import ColorBox from './ColorBox'
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import {ChromePicker} from 'react-color'


class NewPaletteForm extends Component {
    constructor(props){
        super(props);
        this.state = {
            colors: [
                { name: "red", color: "#FF0000"}
            ],
            bgColor: "#FF0000",
          
        }
        this.handleChange = this.handleChange.bind(this)
        this.addColorName = this.addColorName.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)

    }
    handleChange(color) {
        this.setState(st =>({ 
            colors: [
                { 
                    name: st.name, 
                    color:color.hex
            }
            ],
             bgColor: color.hex 
            }));
    };

    addNewColor(color){
        this.setState({

        })
    }

    addColorName(evt){
        // let colorName = this.state.colors.name;
       
       let newColor = {
        [evt.target.name]: evt.target.value,
        color: "yellow"

       }
    
        this.setState(st => ({
            colors: [{
                
                    name: evt.target.value,
                    color: "yellow"
            }
            ]
        }))
    
    }
    handleSubmit(evt){

    }

 
    render(){

 
        return(
            <Grid container justify="center" className = "NewPaletteForm" style={{height: "100vh"}}>
                <Grid container justify="center" style={{backgroundColor:"whitesmoke", width: "15%",  flexDirection: "column", alignItems: "center"}}>
                    <h2>Design Your Palette</h2>
                    <Grid item style= {{margin:"30px 0"}}>
                     
                            <Button variant="contained" color="primary">
                                Random Color
                            </Button>
                            <Button variant="contained" color="secondary">
                                Clear Palette
                            </Button>
                    
                    </Grid>
                    <Grid item>
                    
                        <ChromePicker color={this.state.colors.color}
                                    onChangeComplete = {this.handleChange}
                                    style={{margin: "30px auto"}}
                        />
                        <form onSubmit = {this.handleSubmit}>  
                            <TextField
                                id="filled-with-placeholder"
                                label="Color Name"
                                placeholder="Color Name"
                                margin="normal"
                                variant="filled"
                                value = {this.state.colors.name}
                                name="name"
                                onChange={this.addColorName}
                            />
                            <Button style={{backgroundColor: this.state.colors.color}}>
                                Add Color
                            </Button>
                        </form>
                        
                      
                    </Grid>
                </Grid>
            
                <Grid container style={{width: "85%", flexDirection: "column" }}>
                    <Grid item style={{backgroundColor: "white", padding: "10px", height: "auto"}}>
                        <h5 style={{display: "inline-block", textAlign: "left", width: "50%" }}>Create A Palette</h5>
                        <div style={{display: "inline-block", textAlign: "right", width: "50%"}}>
                            <Button variant="contained" color="primary">
                                <Link to = "/" style={{color: "white"}}> Go Back</Link>
                            </Button>
                            <Button variant="contained" color="secondary">
                                Save
                            </Button>
                            
                        </div>
                    </Grid>
                    <Grid item> 
                        {this.state.colors.map(color => (
                            <ColorBox colorCode = {color.color}
                                    colorName = {color.name}
                            />
                        ))}
                        
                    </Grid>
                </Grid>
            </Grid>
         
        )
    }
}

export default NewPaletteForm;
