import React, { Component } from 'react';
import { Link } from 'react-router-dom'
import {CopyToClipboard} from 'react-copy-to-clipboard'
// import ColorBox from './ColorBox'
import PaletteFooter from './PaletteFooter'
import PaletteNavbar from './PaletteNavbar'
import Grid from '@material-ui/core/Grid';
import chroma from 'chroma-js'

class ColorPalette extends Component {
    constructor(props){
        super(props);
        this.state={
            copied: false
        }
        this.onCopy = this.onCopy.bind(this)
    }

    onCopy(){
        this.setState({
            copied: true
        })
    }

    render(){

        const palette = this.props.palette;
                      
        return(
            <div className="ColorPalette">
         
               <PaletteNavbar handleFormat = {this.props.handleFormat} format = {this.props.format} />
               <div>
                    <Link to = "/palette">Go back</Link>
                </div>
               <Grid container spacing={0}>
                    {
                        palette.colors.map(color => (
                            <Grid item xs={12} sm={3} style={{height: "25vh",position: "relative", 
                                backgroundColor: color.color,
                                    color: chroma.contrast(color.color, 'white') >= 4.5 ? "white" : "black"
                                }}>
                            
                                 <p>{color.name}</p>
                               
                                 <p>{
                                        (this.props.format === 'hex' &&  chroma(color.color).hex()) ||
                                        (this.props.format === "rgb" && chroma(color.color).css()) ||
                                        (this.props.format === "rgba" && chroma(color.color).alpha(0.9).css())  
                                    }
                                </p>

                                <CopyToClipboard onCopy = {this.onCopy} text={(this.props.format === 'hex' &&  chroma(color.color).hex()) ||
                                        (this.props.format === "rgb" && chroma(color.color).css()) ||
                                        (this.props.format === "rgba" && chroma(color.color).alpha(0.9).css()) }>
                                    <button>Copy</button>
                                </CopyToClipboard>
                                                    
                                <Link to = {`/palette/${palette.id}/${color.name}`} style={{ color: chroma.contrast(color.color, 'white') >= 4.5 ? "white" : "black"}}>More</Link>
                            </Grid> 
                        ))
                    }
                </Grid>

                <PaletteFooter paletteName = {palette.paletteName} />
            </div>
        )
    }
}

export default ColorPalette;
